import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

client/src/hooks/useCanvas.ts.content:
import { useState, useCallback, useRef } from 'react';
import { CanvasTool, BrushSettings } from '@/lib/canvas/tools';

interface CanvasData {
  width: number;
  height: number;
  pixels: Uint8ClampedArray;
}

export function useCanvas(width: number, height: number) {
  const [canvasData, setCanvasData] = useState<CanvasData>({
    width,
    height,
    pixels: new Uint8ClampedArray(width * height * 4)
  });

  const [history, setHistory] = useState<CanvasData[]>([]);
  const [historyIndex, setHistoryIndex] = useState(-1);
  const canvasRef = useRef<HTMLCanvasElement | null>(null);

  const initializeCanvas = useCallback((canvas: HTMLCanvasElement) => {
    canvasRef.current = canvas;
    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Fill with transparent white
    ctx.fillStyle = '#FFFFFF';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    const imageData = ctx.getImageData(0, 0, width, height);
    const newCanvasData = {
      width,
      height,
      pixels: new Uint8ClampedArray(imageData.data)
    };

    setCanvasData(newCanvasData);
    addToHistory(newCanvasData);
  }, [width, height]);

  const addToHistory = useCallback((data: CanvasData) => {
    setHistory(prev => {
      const newHistory = prev.slice(0, historyIndex + 1);
      newHistory.push({ ...data, pixels: new Uint8ClampedArray(data.pixels) });
      return newHistory.slice(-50); // Keep last 50 states
    });
    setHistoryIndex(prev => Math.min(prev + 1, 49));
  }, [historyIndex]);

  const drawOnCanvas = useCallback((x: number, y: number, tool: CanvasTool, settings: BrushSettings) => {
    if (!canvasRef.current) return;

    const ctx = canvasRef.current.getContext('2d');
    if (!ctx) return;

    const scale = canvasRef.current.width / width;
    const canvasX = x * scale;
    const canvasY = y * scale;

    ctx.save();

    switch (tool) {
      case 'brush':
      case 'pencil':
        ctx.globalAlpha = settings.opacity;
        ctx.fillStyle = settings.color;
        ctx.beginPath();
        ctx.arc(canvasX, canvasY, (settings.size * scale) / 2, 0, Math.PI * 2);
        ctx.fill();
        break;

      case 'eraser':
        ctx.globalCompositeOperation = 'destination-out';
        ctx.beginPath();
        ctx.arc(canvasX, canvasY, (settings.size * scale) / 2, 0, Math.PI * 2);
        ctx.fill();
        break;

      case 'fill':
        // Simple flood fill implementation
        ctx.fillStyle = settings.color;
        ctx.fillRect(0, 0, canvasRef.current.width, canvasRef.current.height);
        break;

      case 'rectangle':
        ctx.strokeStyle = settings.color;
        ctx.lineWidth = 2;
        ctx.strokeRect(canvasX - 10, canvasY - 10, 20, 20);
        break;

      case 'circle':
        ctx.strokeStyle = settings.color;
        ctx.lineWidth = 2;
        ctx.beginPath();
        ctx.arc(canvasX, canvasY, 10, 0, Math.PI * 2);
        ctx.stroke();
        break;
    }

    ctx.restore();

    // Update canvas data - capture at logical texture dimensions
    const imageData = ctx.getImageData(0, 0, canvasRef.current.width, canvasRef.current.height);

    // Create a temporary canvas at the logical texture size to scale down the data
    const tempCanvas = document.createElement('canvas');
    tempCanvas.width = width;
    tempCanvas.height = height;
    const tempCtx = tempCanvas.getContext('2d');
    if (tempCtx) {
      tempCtx.drawImage(canvasRef.current, 0, 0, width, height);
      const scaledImageData = tempCtx.getImageData(0, 0, width, height);

      const newCanvasData = {
        width,
        height,
        pixels: new Uint8ClampedArray(scaledImageData.data)
      };

      setCanvasData(newCanvasData);
      addToHistory(newCanvasData);
    }
  }, [width, height, addToHistory]);

  const undo = useCallback(() => {
    if (historyIndex > 0) {
      const newIndex = historyIndex - 1;
      const previousState = history[newIndex];
      setCanvasData(previousState);
      setHistoryIndex(newIndex);

      if (canvasRef.current) {
        const ctx = canvasRef.current.getContext('2d');
        if (ctx) {
          // Create temporary canvas at logical size and restore the data
          const tempCanvas = document.createElement('canvas');
          tempCanvas.width = width;
          tempCanvas.height = height;
          const tempCtx = tempCanvas.getContext('2d');

          if (tempCtx) {
            const imageData = new ImageData(previousState.pixels, width, height);
            tempCtx.putImageData(imageData, 0, 0);

            // Scale up to display canvas
            ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
            ctx.drawImage(tempCanvas, 0, 0, canvasRef.current.width, canvasRef.current.height);
          }
        }
      }
    }
  }, [history, historyIndex, width, height]);

  const redo = useCallback(() => {
    if (historyIndex < history.length - 1) {
      const newIndex = historyIndex + 1;
      const nextState = history[newIndex];
      setCanvasData(nextState);
      setHistoryIndex(newIndex);

      if (canvasRef.current) {
        const ctx = canvasRef.current.getContext('2d');
        if (ctx) {
          // Create temporary canvas at logical size and restore the data
          const tempCanvas = document.createElement('canvas');
          tempCanvas.width = width;
          tempCanvas.height = height;
          const tempCtx = tempCanvas.getContext('2d');

          if (tempCtx) {
            const imageData = new ImageData(nextState.pixels, width, height);
            tempCtx.putImageData(imageData, 0, 0);

            // Scale up to display canvas
            ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
            ctx.drawImage(tempCanvas, 0, 0, canvasRef.current.width, canvasRef.current.height);
          }
        }
      }
    }
  }, [history, historyIndex, width, height]);

  const clearCanvas = useCallback(() => {
    if (!canvasRef.current) return;

    const ctx = canvasRef.current.getContext('2d');
    if (!ctx) return;

    ctx.fillStyle = '#FFFFFF';
    ctx.fillRect(0, 0, canvasRef.current.width, canvasRef.current.height);

    // Create a temporary canvas at the logical texture size to capture data properly
    const tempCanvas = document.createElement('canvas');
    tempCanvas.width = width;
    tempCanvas.height = height;
    const tempCtx = tempCanvas.getContext('2d');
    if (tempCtx) {
      tempCtx.fillStyle = '#FFFFFF';
      tempCtx.fillRect(0, 0, width, height);
      const scaledImageData = tempCtx.getImageData(0, 0, width, height);

      const newCanvasData = {
        width,
        height,
        pixels: new Uint8ClampedArray(scaledImageData.data)
      };

      setCanvasData(newCanvasData);
      addToHistory(newCanvasData);
    }
  }, [width, height, addToHistory]);

  const exportCanvas = useCallback(async (format: 'png' | 'jpg' = 'png'): Promise<Blob | null> => {
    if (!canvasRef.current) return null;

    // Create a temporary canvas at the logical texture size for export
    const tempCanvas = document.createElement('canvas');
    tempCanvas.width = width;
    tempCanvas.height = height;
    const tempCtx = tempCanvas.getContext('2d');

    if (!tempCtx) return null;

    // Draw the display canvas onto the temp canvas at logical dimensions
    tempCtx.drawImage(canvasRef.current, 0, 0, width, height);

    return new Promise((resolve) => {
      tempCanvas.toBlob((blob) => {
        resolve(blob);
      }, `image/${format}`);
    });
  }, [width, height]);

  return {
    canvasData,
    history,
    historyIndex,
    initializeCanvas,
    drawOnCanvas,
    undo,
    redo,
    clearCanvas,
    exportCanvas
  };
}

