import { useState, useEffect } from "react";
import { useIsMobile } from "@/hooks/use-mobile";
import Sidebar from "@/components/Layout/Sidebar";
import Header from "@/components/Layout/Header";
import EntityBuilder from "@/components/Builders/EntityBuilder";
import BlockBuilder from "@/components/Builders/BlockBuilder";
import ItemBuilder from "@/components/Builders/ItemBuilder";
import RecipeBuilder from "@/components/Builders/RecipeBuilder";
import LootTableBuilder from "@/components/Builders/LootTableBuilder";
import BiomeBuilder from "@/components/Builders/BiomeBuilder";
import SpawnRuleBuilder from "@/components/Builders/SpawnRuleBuilder";
import ClientEntityBuilder from "@/components/Builders/ClientEntityBuilder";
import TextureCreator from "@/components/Tools/TextureCreator";
import ScriptStudio from "@/components/Tools/ScriptStudio";
import AddonPackager from "@/components/Tools/AddonPackager";
import JSONValidator from "@/components/Tools/JSONValidator";
import ExternalTools from "@/components/Tools/ExternalTools";
import Documentation from "@/components/Documentation/Documentation";
import InteractiveTutorial from "@/components/Tutorial/InteractiveTutorial";
import QuickStart from "@/components/Tutorial/QuickStart";
import WelcomeSection from "@/components/Welcome/WelcomeSection";
import CommunitySection from "@/components/Community/CommunitySection";

type Section = 
  | 'welcome'
  | 'tutorial'
  | 'quick-start'
  | 'documentation'
  | 'docs-concepts'
  | 'docs-entities'
  | 'docs-blocks'
  | 'docs-items'
  | 'docs-scripting'
  | 'builder-entity'
  | 'builder-client-entity'
  | 'builder-block'
  | 'builder-item'
  | 'builder-recipe'
  | 'builder-loot'
  | 'builder-biome'
  | 'builder-spawn'
  | 'script-studio'
  | 'addon-packager'
  | 'validator'
  | 'external-tools'
  | 'community'
  | 'omni-science';

export default function Home() {
  const [currentSection, setCurrentSection] = useState<Section>('welcome');
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const isMobile = useIsMobile();

  // Auto-collapse sidebar on mobile
  useEffect(() => {
    if (isMobile && !sidebarCollapsed) {
      setSidebarCollapsed(true);
    }
  }, [isMobile]);

  const handleNavigate = (section: string) => {
    setCurrentSection(section as Section);
  };

  const handleMenuToggle = () => {
    setSidebarCollapsed(!sidebarCollapsed);
  };

  const renderSection = () => {
    switch (currentSection) {
      case 'welcome':
        return <WelcomeSection onNavigate={handleNavigate} />;
      case 'tutorial':
        return <InteractiveTutorial onNavigate={handleNavigate} />;
      case 'quick-start':
        return <QuickStart onNavigate={handleNavigate} />;
      case 'documentation':
        return <Documentation onNavigate={handleNavigate} />;

      // Documentation-specific routing - map sidebar IDs to documentation tabs
      case 'docs-concepts':
        return <Documentation onNavigate={handleNavigate} initialTab="getting-started" />;
      case 'docs-entities':
        return <Documentation onNavigate={handleNavigate} initialTab="entity-development" />;
      case 'docs-blocks':
        return <Documentation onNavigate={handleNavigate} initialTab="block-development" />;
      case 'docs-items':
        return <Documentation onNavigate={handleNavigate} initialTab="item-development" />;
      case 'docs-scripting':
        return <Documentation onNavigate={handleNavigate} initialTab="scripting-apis" />;

      case 'builder-entity':
        return <EntityBuilder />;
      case 'builder-client-entity':
        return <ClientEntityBuilder />;
      case 'builder-block':
        return <BlockBuilder />;
      case 'builder-item':
        return <ItemBuilder />;
      case 'builder-recipe':
        return <RecipeBuilder />;
      case 'builder-loot':
        return <LootTableBuilder />;
      case 'builder-biome':
        return <BiomeBuilder />;
      case 'builder-spawn':
        return <SpawnRuleBuilder />;
      case 'script-studio':
        return <ScriptStudio />;
      case 'addon-packager':
        return <AddonPackager />;
      case 'validator':
        return <JSONValidator />;
      case 'external-tools':
        return <ExternalTools />;
      case 'community':
        return <CommunitySection />;
      case 'omni-science':
        return <CommunitySection />;
      default:
        return <WelcomeSection onNavigate={handleNavigate} />;
    }
  };

  return (
    <div className="flex h-screen bg-background text-foreground overflow-hidden">
      <Sidebar 
        currentSection={currentSection}
        onSectionChange={handleNavigate}
        collapsed={sidebarCollapsed}
        onToggleCollapse={handleMenuToggle}
      />
      <main className="flex-1 flex flex-col min-w-0 h-full">
        <Header 
          currentSection={currentSection} 
          onMenuToggle={isMobile ? handleMenuToggle : undefined}
          isSidebarOpen={!sidebarCollapsed}
        />
        <div className="flex-1 overflow-y-auto min-h-0">
          <div className="fadeIn p-3 sm:p-4 md:p-6 min-h-full">
            {renderSection()}
          </div>
        </div>
      </main>
    </div>
  );
}

