client/src/components/Builders/BiomeBuil.content:
import { useState, useMemo } from "react";
import { Mountain, Download, Copy, Save, RotateCcw, Info, Settings, AlertCircle, FileText, Plus, Layers } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Accordion, AccordionContent, AccordionItem, AccordionTrigger } from "@/components/ui/accordion";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "@/components/ui/tooltip";

import CodePreview from "@/components/Common/CodePreview";
import ValidationStatus from "@/components/Common/ValidationStatus";
import ComponentSelector, { ComponentItem } from "@/components/Common/ComponentSelector";
import ComponentForm, { ComponentDefinition } from "@/components/Common/ComponentForm";

import { useLocalStorage } from "@/hooks/useLocalStorage";
import { useToast } from "@/hooks/use-toast";
import { useCollapsible } from "@/hooks/useCollapsible";
import { CollapsibleSection, CollapsibleGroup } from "@/components/ui/collapsible-section";
import { validateBiomeJSON } from "@/lib/minecraft/validation";
import { 
  ComponentInstance, 
  generateComponentsJSON, 
  applyPreset, 
  getRecommendedComponents,
  checkComponentCompatibility,
  validateComponentProperties 
} from "@/lib/minecraft/componentUtils";

// Import the comprehensive biome and spawn rule registries
import { biomeComponents, spawnRuleComponents, generateBiomeJSON, generateSpawnRulesJSON } from "@shared/gameplayRegistry";

interface SpawnEntity {
  type: string;
  weight: number;
  minGroupSize: number;
  maxGroupSize: number;
  components: ComponentInstance[];
}

export default function BiomeBuilder() {
  const { toast } = useToast();

  // Basic biome properties
  const [identifier, setIdentifier] = useLocalStorage('biome-identifier-v2', '');
  const [displayName, setDisplayName] = useLocalStorage('biome-display-name-v2', '');
  const [description, setDescription] = useLocalStorage('biome-description-v2', '');

  // Component management for biome
  const [biomeComponentInstances, setBiomeComponentInstances] = useLocalStorage<ComponentInstance[]>('biome-components-v2', [
    {
      name: 'minecraft:climate',
      enabled: true,
      properties: { temperature: 0.8, downfall: 0.4 },
      metadata: { addedAt: Date.now(), category: 'Climate', difficulty: 'intermediate' }
    },
    {
      name: 'minecraft:surface_parameters',
      enabled: true,
      properties: { 
        top_material: 'minecraft:grass',
        mid_material: 'minecraft:dirt'
      },
      metadata: { addedAt: Date.now(), category: 'Surface', difficulty: 'advanced' }
    }
  ]);

  // Spawn entity management
  const [spawnEntities, setSpawnEntities] = useLocalStorage<SpawnEntity[]>('biome-spawn-entities-v2', [
    {
      type: 'minecraft:cow',
      weight: 10,
      minGroupSize: 4,
      maxGroupSize: 4,
      components: [
        {
          name: 'minecraft:spawns_on_surface',
          enabled: true,
          properties: {},
          metadata: { addedAt: Date.now(), category: 'Surface', difficulty: 'intermediate' }
        },
        {
          name: 'minecraft:weight',
          enabled: true,
          properties: { default: 10 },
          metadata: { addedAt: Date.now(), category: 'Probability', difficulty: 'beginner' }
        }
      ]
    }
  ]);

  // UI state
  const [activeTab, setActiveTab] = useState('basic');
  const [selectedComponent, setSelectedComponent] = useState<string | null>(null);
  const [showComponentSelector, setShowComponentSelector] = useState(false);
  const [showComponentForm, setShowComponentForm] = useState(false);
  const [editingComponent, setEditingComponent] = useState<ComponentInstance | null>(null);
  const [componentTargetType, setComponentTargetType] = useState<'biome' | 'spawn'>('biome');
  const [selectedEntityIndex, setSelectedEntityIndex] = useState<number>(0);

  // Convert registry components to ComponentItem format
  const availableBiomeComponents: ComponentItem[] = useMemo(() => {
    return biomeComponents.map(comp => ({
      name: comp.name,
      description: comp.description,
      category: comp.category,
      subcategory: comp.subcategory,
      version: comp.version,
      difficulty: comp.difficulty,
      properties: comp.properties,
      example: comp.example,
      keywords: comp.keywords,
      stability: comp.stability,
      dependencies: comp.dependencies,
      conflicts: comp.conflicts
    }));
  }, []);

  const availableSpawnComponents: ComponentItem[] = useMemo(() => {
    return spawnRuleComponents.map(comp => ({
      name: comp.name,
      description: comp.description,
      category: comp.category,
      subcategory: comp.subcategory,
      version: comp.version,
      difficulty: comp.difficulty,
      properties: comp.properties,
      example: comp.example,
      keywords: comp.keywords,
      stability: comp.stability,
      dependencies: comp.dependencies,
      conflicts: comp.conflicts
    }));
  }, []);

  // Get selected component names
  const selectedBiomeComponentNames = biomeComponentInstances.map(c => c.name);

  // Generate JSON for biome
  const biomeJSON = useMemo(() => {
    const componentsJSON = generateComponentsJSON(biomeComponentInstances);

    return {
      format_version: "1.21.0",
      "minecraft:biome": {
        description: {
          identifier: identifier || "my_addon:custom_biome"
        },
        components: componentsJSON
      }
    };
  }, [identifier, biomeComponentInstances]);

  // Generate JSON for spawn rules
  const spawnRulesJSON = useMemo(() => {
    if (spawnEntities.length === 0) return null;

    const spawnRules = spawnEntities.map(entity => {
      const entityComponents = generateComponentsJSON(entity.components);

      return {
        description: {
          identifier: `${identifier || "my_addon:custom_biome"}_${entity.type.replace('minecraft:', '')}_spawn`
        },
        conditions: [
          {
            ...entityComponents,
            "minecraft:permute_type": [
              {
                entity_type: entity.type,
                weight: entity.weight,
                min_count: entity.minGroupSize,
                max_count: entity.maxGroupSize
              }
            ]
          }
        ]
      };
    });

    return {
      format_version: "1.21.0",
      "minecraft:spawn_rules": spawnRules
    };
  }, [identifier, spawnEntities]);

  const biomeValidation = validateBiomeJSON(biomeJSON);
  const spawnValidation = spawnRulesJSON ? validateBiomeJSON(spawnRulesJSON) : { isValid: true, errors: [] };

  const recommendedBiomeComponents = getRecommendedComponents(biomeComponentInstances, availableBiomeComponents, 3);

  // Component management functions
  const addBiomeComponent = (componentName: string) => {
    const componentDef = availableBiomeComponents.find(c => c.name === componentName);
    if (!componentDef) return;

    // Check compatibility
    const compatibility = checkComponentCompatibility(componentDef, biomeComponentInstances, availableBiomeComponents);
    if (!compatibility.compatible) {
      toast({
        title: "Cannot add component",
        description: compatibility.issues.join(', '),
        variant: "destructive"
      });
      return;
    }

    // Generate default properties
    const defaultProperties: Record<string, any> = {};
    componentDef.properties.forEach(prop => {
      if (prop.default !== undefined) {
        defaultProperties[prop.name] = prop.default;
      } else {
        switch (prop.type) {
          case 'number':
            defaultProperties[prop.name] = prop.min ?? prop.example ?? 0;
            break;
          case 'boolean':
            defaultProperties[prop.name] = prop.example ?? false;
            break;
          case 'string':
            defaultProperties[prop.name] = prop.options?.[0] ?? prop.example ?? '';
            break;
          case 'range':
            defaultProperties[prop.name] = prop.example ?? { min: 0, max: 1 };
            break;
          case 'vector3':
            defaultProperties[prop.name] = prop.example ?? { x: 0, y: 0, z: 0 };
            break;
          case 'array':
            defaultProperties[prop.name] = prop.example ?? [];
            break;
          case 'object':
            defaultProperties[prop.name] = prop.example ?? {};
            break;
        }
      }
    });

    const newComponent: ComponentInstance = {
      name: componentName,
      enabled: true,
      properties: defaultProperties,
      metadata: {
        addedAt: Date.now(),
        category: componentDef.category,
        difficulty: componentDef.difficulty
      }
    };

    setBiomeComponentInstances([...biomeComponentInstances, newComponent]);
    setShowComponentSelector(false);

    toast({
      title: "Component added",
      description: `${componentName} has been added to the biome.`
    });
  };

  const removeBiomeComponent = (componentName: string) => {
    setBiomeComponentInstances(biomeComponentInstances.filter(c => c.name !== componentName));
  };

  const updateBiomeComponent = (componentName: string, newProperties: Record<string, any>) => {
    setBiomeComponentInstances(biomeComponentInstances.map(comp => 
      comp.name === componentName 
        ? { ...comp, properties: newProperties }
        : comp
    ));
  };

  const toggleBiomeComponent = (componentName: string) => {
    setBiomeComponentInstances(biomeComponentInstances.map(comp =>
      comp.name === componentName 
        ? { ...comp, enabled: !comp.enabled }
        : comp
    ));
  };

  // Spawn entity management
  const addSpawnEntity = () => {
    const newEntity: SpawnEntity = {
      type: 'minecraft:cow',
      weight: 10,
      minGroupSize: 1,
      maxGroupSize: 4,
      components: [
        {
          name: 'minecraft:spawns_on_surface',
          enabled: true,
          properties: {},
          metadata: { addedAt: Date.now(), category: 'Surface', difficulty: 'intermediate' }
        }
      ]
    };
    setSpawnEntities([...spawnEntities, newEntity]);
  };

  const removeSpawnEntity = (index: number) => {
    setSpawnEntities(spawnEntities.filter((_, i) => i !== index));
    if (selectedEntityIndex >= spawnEntities.length - 1) {
      setSelectedEntityIndex(Math.max(0, index - 1));
    }
  };

  const updateSpawnEntity = (index: number, updates: Partial<SpawnEntity>) => {
    setSpawnEntities(spawnEntities.map((entity, i) => 
      i === index ? { ...entity, ...updates } : entity
    ));
  };

  const openComponentForm = (component: ComponentInstance) => {
    setEditingComponent(component);
    setShowComponentForm(true);
  };

  const handleComponentFormSubmit = (values: Record<string, any>) => {
    if (editingComponent) {
      if (componentTargetType === 'biome') {
        updateBiomeComponent(editingComponent.name, values);
      } else {
        // Update spawn component for selected entity
        const entity = spawnEntities[selectedEntityIndex];
        const updatedComponents = entity.components.map(comp =>
          comp.name === editingComponent.name ? { ...comp, properties: values } : comp
        );
        updateSpawnEntity(selectedEntityIndex, { components: updatedComponents });
      }

      setShowComponentForm(false);
      setEditingComponent(null);

      toast({
        title: "Component updated",
        description: `${editingComponent.name} has been updated successfully.`
      });
    }
  };

  const resetBiome = () => {
    setBiomeComponentInstances([
      {
        name: 'minecraft:climate',
        enabled: true,
        properties: { temperature: 0.8, downfall: 0.4 },
        metadata: { addedAt: Date.now(), category: 'Climate', difficulty: 'intermediate' }
      }
    ]);
    setSpawnEntities([]);
    setIdentifier('');
    setDisplayName('');
    setDescription('');

    toast({
      title: "Biome reset",
      description: "Biome has been reset to default state."
    });
  };

  const exportToClipboard = (content: any, type: string) => {
    navigator.clipboard.writeText(JSON.stringify(content, null, 2));
    toast({
      title: "Copied to clipboard",
      description: `${type} JSON has been copied to your clipboard.`
    });
  };

  const biomeComponentsByCategory = useMemo(() => {
    const grouped: Record<string, ComponentInstance[]> = {};
    biomeComponentInstances.forEach(comp => {
      const category = comp.metadata?.category || 'Other';
      if (!grouped[category]) grouped[category] = [];
      grouped[category].push(comp);
    });
    return grouped;
  }, [biomeComponentInstances]);

  const getComponentDefinition = (componentName: string, isSpawnComponent: boolean = false): ComponentDefinition | null => {
    const components = isSpawnComponent ? availableSpawnComponents : availableBiomeComponents;
    const def = components.find(c => c.name === componentName);
    if (!def) return null;

    return {
      name: def.name,
      description: def.description,
      category: def.category,
      subcategory: def.subcategory,
      version: def.version,
      difficulty: def.difficulty,
      properties: def.properties,
      example: def.example,
      keywords: def.keywords,
      stability: def.stability,
      dependencies: def.dependencies,
      conflicts: def.conflicts
    };
  };

  return (
    <TooltipProvider>
      <section className="p-6" data-testid="biome-builder">
        <div className="max-w-7xl mx-auto">
          <div className="grid lg:grid-cols-2 gap-6">
            {/* Builder Form */}
            <div className="space-y-6">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Mountain className="w-5 h-5" />
                    Biome Configuration
                  </CardTitle>
                  <CardDescription>
                    Create comprehensive Minecraft Bedrock biomes with spawn rules
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <Tabs value={activeTab} onValueChange={setActiveTab}>
                    <TabsList className="grid w-full grid-cols-4">
                      <TabsTrigger value="basic" data-testid="tab-basic">Basic</TabsTrigger>
                      <TabsTrigger value="components" data-testid="tab-components">
                        Components ({biomeComponentInstances.length})
                      </TabsTrigger>
                      <TabsTrigger value="spawning" data-testid="tab-spawning">
                        Spawning ({spawnEntities.length})
                      </TabsTrigger>
                      <TabsTrigger value="advanced" data-testid="tab-advanced">Advanced</TabsTrigger>
                    </TabsList>

                    <TabsContent value="basic" className="space-y-4">
                      <div className="grid gap-4">
                        <div>
                          <Label htmlFor="biome-identifier">Biome Identifier *</Label>
                          <Input
                            id="biome-identifier"
                            value={identifier}
                            onChange={(e) => setIdentifier(e.target.value)}
                            placeholder="my_addon:custom_biome"
                            data-testid="input-identifier"
                          />
                          <p className="text-xs text-muted-foreground mt-1">
                            Must include namespace (e.g., my_addon:biome_name)
                          </p>
                        </div>

                        <div>
                          <Label htmlFor="biome-display-name">Display Name</Label>
                          <Input
                            id="biome-display-name"
                            value={displayName}
                            onChange={(e) => setDisplayName(e.target.value)}
                            placeholder="Custom Biome"
                            data-testid="input-display-name"
                          />
                        </div>

                        <div>
                          <Label htmlFor="biome-description">Description</Label>
                          <Input
                            id="biome-description"
                            value={description}
                            onChange={(e) => setDescription(e.target.value)}
                            placeholder="A custom biome for my addon"
                            data-testid="input-description"
                          />
                        </div>
                      </div>
                    </TabsContent>

                    <TabsContent value="components" className="space-y-4">
                      <div className="flex items-center justify-between">
                        <h4 className="font-medium">Biome Components</h4>
                        <Button 
                          onClick={() => {
                            setComponentTargetType('biome');
                            setShowComponentSelector(true);
                          }}
                          data-testid="button-add-component"
                        >
                          Add Component
                        </Button>
                      </div>

                      {recommendedBiomeComponents.length > 0 && (
                        <Alert>
                          <Info className="h-4 w-4" />
                          <AlertDescription>
                            <div className="space-y-2">
                              <p className="font-medium">Recommended components:</p>
                              <div className="flex flex-wrap gap-1">
                                {recommendedBiomeComponents.map(comp => (
                                  <Button
                                    key={comp.name}
                                    variant="outline"
                                    size="sm"
                                    onClick={() => addBiomeComponent(comp.name)}
                                    data-testid={`button-add-recommended-${comp.name}`}
                                  >
                                    {comp.name}
                                  </Button>
                                ))}
                              </div>
                            </div>
                          </AlertDescription>
                        </Alert>
                      )}

                      <ScrollArea className="h-[400px]">
                        <Accordion type="single" collapsible className="space-y-2">
                          {Object.entries(biomeComponentsByCategory).map(([category, categoryComponents]) => (
                            <AccordionItem key={category} value={category}>
                              <AccordionTrigger className="text-sm" data-testid={`accordion-${category}`}>
                                {category} ({categoryComponents.length})
                              </AccordionTrigger>
                              <AccordionContent>
                                <div className="space-y-2">
                                  {categoryComponents.map((component) => {
                                    const def = getComponentDefinition(component.name);
                                    return (
                                      <Card key={component.name} className="p-3">
                                        <div className="flex items-start justify-between gap-2">
                                          <div className="flex-1 min-w-0">
                                            <div className="flex items-center gap-2 mb-1">
                                              <Switch
                                                checked={component.enabled}
                                                onCheckedChange={() => toggleBiomeComponent(component.name)}
                                                data-testid={`switch-${component.name}`}
                                              />
                                              <span className="font-medium text-sm truncate">
                                                {component.name}
                                              </span>
                                              {def && (
                                                <Badge 
                                                  variant="outline" 
                                                  className={`text-xs ${
                                                    def.difficulty === 'beginner' ? 'border-green-500' :
                                                    def.difficulty === 'intermediate' ? 'border-yellow-500' :
                                                    'border-red-500'
                                                  }`}
                                                >
                                                  {def.difficulty}
                                                </Badge>
                                              )}
                                            </div>
                                            {def && (
                                              <p className="text-xs text-muted-foreground line-clamp-2">
                                                {def.description}
                                              </p>
                                            )}
                                          </div>
                                          <div className="flex gap-1">
                                            <Tooltip>
                                              <TooltipTrigger asChild>
                                                <Button
                                                  variant="ghost"
                                                  size="sm"
                                                  onClick={() => {
                                                    setComponentTargetType('biome');
                                                    openComponentForm(component);
                                                  }}
                                                  disabled={!component.enabled}
                                                  data-testid={`button-configure-${component.name}`}
                                                >
                                                  <Settings className="w-3 h-3" />
                                                </Button>
                                              </TooltipTrigger>
                                              <TooltipContent>Configure properties</TooltipContent>
                                            </Tooltip>
                                            <Tooltip>
                                              <TooltipTrigger asChild>
                                                <Button
                                                  variant="ghost"
                                                  size="sm"
                                                  onClick={() => removeBiomeComponent(component.name)}
                                                  data-testid={`button-remove-${component.name}`}
                                                >
                                                  <AlertCircle className="w-3 h-3" />
                                                </Button>
                                              </TooltipTrigger>
                                              <TooltipContent>Remove component</TooltipContent>
                                            </Tooltip>
                                          </div>
                                        </div>
                                      </Card>
                                    );
                                  })}
                                </div>
                              </AccordionContent>
                            </AccordionItem>
                          ))}
                        </Accordion>
                      </ScrollArea>
                    </TabsContent>

                    <TabsContent value="spawning" className="space-y-4">
                      <div className="flex items-center justify-between">
                        <h4 className="font-medium">Spawn Entities</h4>
                        <Button onClick={addSpawnEntity} data-testid="button-add-spawn-entity">
                          <Plus className="w-4 h-4 mr-2" />
                          Add Entity
                        </Button>
                      </div>

                      <ScrollArea className="h-[400px]">
                        {spawnEntities.map((entity, index) => (
                          <Card key={index} className="p-4 mb-4">
                            <div className="space-y-4">
                              <div className="flex items-center justify-between">
                                <h5 className="font-medium">Entity {index + 1}</h5>
                                <Button
                                  variant="destructive"
                                  size="sm"
                                  onClick={() => removeSpawnEntity(index)}
                                  data-testid={`button-remove-entity-${index}`}
                                >
                                  <AlertCircle className="w-3 h-3" />
                                </Button>
                              </div>

                              <div className="grid grid-cols-2 gap-2">
                                <div>
                                  <Label className="text-xs">Entity Type</Label>
                                  <Input
                                    value={entity.type}
                                    onChange={(e) => updateSpawnEntity(index, { type: e.target.value })}
                                    placeholder="minecraft:cow"
                                    data-testid={`input-entity-type-${index}`}
                                  />
                                </div>
                                <div>
                                  <Label className="text-xs">Weight</Label>
                                  <Input
                                    type="number"
                                    value={entity.weight}
                                    onChange={(e) => updateSpawnEntity(index, { weight: parseInt(e.target.value) || 10 })}
                                    data-testid={`input-entity-weight-${index}`}
                                  />
                                </div>
                                <div>
                                  <Label className="text-xs">Min Group Size</Label>
                                  <Input
                                    type="number"
                                    value={entity.minGroupSize}
                                    onChange={(e) => updateSpawnEntity(index, { minGroupSize: parseInt(e.target.value) || 1 })}
                                    data-testid={`input-entity-min-group-${index}`}
                                  />
                                </div>
                                <div>
                                  <Label className="text-xs">Max Group Size</Label>
                                  <Input
                                    type="number"
                                    value={entity.maxGroupSize}
                                    onChange={(e) => updateSpawnEntity(index, { maxGroupSize: parseInt(e.target.value) || 4 })}
                                    data-testid={`input-entity-max-group-${index}`}
                                  />
                                </div>
                              </div>

                              <div>
                                <div className="flex items-center justify-between mb-2">
                                  <Label className="text-xs">Spawn Components ({entity.components.length})</Label>
                                  <Button
                                    variant="outline"
                                    size="sm"
                                    onClick={() => {
                                      setSelectedEntityIndex(index);
                                      setComponentTargetType('spawn');
                                      setShowComponentSelector(true);
                                    }}
                                    data-testid={`button-add-spawn-component-${index}`}
                                  >
                                    <Plus className="w-3 h-3 mr-1" />
                                    Add Component
                                  </Button>
                                </div>
                                {entity.components.map((component, compIndex) => (
                                  <div key={compIndex} className="flex items-center gap-2 p-2 bg-muted rounded text-xs mb-1">
                                    <Switch
                                      checked={component.enabled}
                                      onCheckedChange={(enabled) => {
                                        const newComponents = [...entity.components];
                                        newComponents[compIndex] = { ...component, enabled };
                                        updateSpawnEntity(index, { components: newComponents });
                                      }}
                                    />
                                    <span className="flex-1 font-mono">{component.name}</span>
                                    <Button
                                      variant="ghost"
                                      size="sm"
                                      onClick={() => {
                                        setSelectedEntityIndex(index);
                                        setComponentTargetType('spawn');
                                        openComponentForm(component);
                                      }}
                                      data-testid={`button-configure-spawn-component-${index}-${compIndex}`}
                                    >
                                      <Settings className="w-3 h-3" />
                                    </Button>
                                  </div>
                                ))}
                              </div>
                            </div>
                          </Card>
                        ))}
                      </ScrollArea>
                    </TabsContent>

                    <TabsContent value="advanced" className="space-y-4">
                      <div className="space-y-4">
                        <div>
                          <h4 className="font-medium mb-2">Actions</h4>
                          <div className="flex gap-2">
                            <Button
                              variant="outline"
                              onClick={resetBiome}
                              data-testid="button-reset"
                            >
                              <RotateCcw className="w-4 h-4 mr-2" />
                              Reset
                            </Button>
                            <Button
                              variant="outline"
                              onClick={() => exportToClipboard(biomeJSON, 'Biome')}
                              data-testid="button-export-biome"
                            >
                              <Copy className="w-4 h-4 mr-2" />
                              Copy Biome JSON
                            </Button>
                            {spawnRulesJSON && (
                              <Button
                                variant="outline"
                                onClick={() => exportToClipboard(spawnRulesJSON, 'Spawn Rules')}
                                data-testid="button-export-spawn"
                              >
                                <FileText className="w-4 h-4 mr-2" />
                                Copy Spawn Rules JSON
                              </Button>
                            )}
                          </div>
                        </div>

                        <div className="space-y-3">
                          <h4 className="font-medium">Biome Statistics</h4>
                          <div className="grid grid-cols-2 gap-4 text-sm">
                            <div className="bg-muted p-3 rounded">
                              <div className="font-medium">Biome Components</div>
                              <div className="text-2xl font-bold">{biomeComponentInstances.length}</div>
                            </div>
                            <div className="bg-muted p-3 rounded">
                              <div className="font-medium">Spawn Entities</div>
                              <div className="text-2xl font-bold">{spawnEntities.length}</div>
                            </div>
                            <div className="bg-muted p-3 rounded">
                              <div className="font-medium">Enabled Components</div>
                              <div className="text-2xl font-bold">{biomeComponentInstances.filter(c => c.enabled).length}</div>
                            </div>
                            <div className="bg-muted p-3 rounded">
                              <div className="font-medium">Total Spawn Weight</div>
                              <div className="text-2xl font-bold">{spawnEntities.reduce((sum, entity) => sum + entity.weight, 0)}</div>
                            </div>
                          </div>
                        </div>

                        <ValidationStatus validation={biomeValidation} />
                        {spawnRulesJSON && (
                          <div>
                            <h5 className="font-medium mb-2">Spawn Rules Validation</h5>
                            <ValidationStatus validation={spawnValidation} />
                          </div>
                        )}
                      </div>
                    </TabsContent>
                  </Tabs>
                </CardContent>
              </Card>
            </div>

            {/* JSON Preview */}
            <div className="space-y-6">
              <CodePreview 
                code={JSON.stringify(biomeJSON, null, 2)}
                language="json"
                title="Biome JSON"
                validation={biomeValidation}
              />

              {spawnRulesJSON && (
                <CodePreview 
                  code={JSON.stringify(spawnRulesJSON, null, 2)}
                  language="json"
                  title="Spawn Rules JSON"
                  validation={spawnValidation}
                />
              )}
            </div>
          </div>
        </div>

        {/* Component Selector Modal */}
        {showComponentSelector && (
          <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
            <div className="bg-background rounded-lg shadow-lg max-w-4xl w-full max-h-[90vh] overflow-hidden">
              <ComponentSelector
                components={componentTargetType === 'biome' ? availableBiomeComponents : availableSpawnComponents}
                selectedComponents={componentTargetType === 'biome' ? selectedBiomeComponentNames : []}
                onAddComponent={componentTargetType === 'biome' ? addBiomeComponent : (componentName) => {
                  // Add spawn component to selected entity
                  const entity = spawnEntities[selectedEntityIndex];
                  const componentDef = availableSpawnComponents.find(c => c.name === componentName);
                  if (componentDef) {
                    const newComponent: ComponentInstance = {
                      name: componentName,
                      enabled: true,
                      properties: {},
                      metadata: {
                        addedAt: Date.now(),
                        category: componentDef.category,
                        difficulty: componentDef.difficulty
                      }
                    };
                    updateSpawnEntity(selectedEntityIndex, {
                      components: [...entity.components, newComponent]
                    });
                    setShowComponentSelector(false);
                  }
                }}
                onRemoveComponent={() => {}}
                title={`Add ${componentTargetType === 'biome' ? 'Biome' : 'Spawn Rule'} Components`}
                description={`Choose from official Minecraft Bedrock ${componentTargetType} components`}
                showCategories={true}
                showCompatibility={true}
              />
              <div className="p-4 border-t flex justify-end">
                <Button 
                  variant="outline" 
                  onClick={() => setShowComponentSelector(false)}
                  data-testid="button-close-selector"
                >
                  Close
                </Button>
              </div>
            </div>
          </div>
        )}

        {/* Component Form Modal */}
        {showComponentForm && editingComponent && (
          <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
            <div className="bg-background rounded-lg shadow-lg max-w-2xl w-full max-h-[90vh] overflow-hidden">
              {(() => {
                const def = getComponentDefinition(editingComponent.name, componentTargetType === 'spawn');
                return def ? (
                  <ComponentForm
                    component={def}
                    initialValues={editingComponent.properties}
                    onSubmit={handleComponentFormSubmit}
                    onCancel={() => {
                      setShowComponentForm(false);
                      setEditingComponent(null);
                    }}
                    isEditing={true}
                    showExample={true}
                  />
                ) : null;
              })()}
            </div>
          </div>
        )}
      </section>
    </TooltipProvider>
  );
}

client/src/components/Builders/BlockBuil.content:
import { useState, useMemo } from "react";
import { Package, Download, Copy, Save, RotateCcw, Info, Zap, AlertCircle, Settings, FileText, Layers, Box } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Accordion, AccordionContent, AccordionItem, AccordionTrigger } from "@/components/ui/accordion";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "@/components/ui/tooltip";

import CodePreview from "@/components/Common/CodePreview";
import ValidationStatus from "@/components/Common/ValidationStatus";
import ComponentSelector, { ComponentItem } from "@/components/Common/ComponentSelector";
import ComponentForm, { ComponentDefinition } from "@/components/Common/ComponentForm";

import { useLocalStorage } from "@/hooks/useLocalStorage";
import { useToast } from "@/hooks/use-toast";
import { validateBlockJSON } from "@/lib/minecraft/validation";
import { 
  ComponentInstance, 
  generateComponentsJSON, 
  applyPreset, 
  getRecommendedComponents,
  checkComponentCompatibility,
  validateComponentProperties,
  BLOCK_PRESETS 
} from "@/lib/minecraft/componentUtils";

// Import the comprehensive block registry
import { blockComponents } from "@shared/blockRegistry";

export default function BlockBuilder() {
  const { toast } = useToast();

  // Basic block properties
  const [identifier, setIdentifier] = useLocalStorage('block-identifier', '');
  const [displayName, setDisplayName] = useLocalStorage('block-display-name', '');
  const [description, setDescription] = useLocalStorage('block-description', '');
  const [registerToCreative, setRegisterToCreative] = useLocalStorage('block-creative', true);
  const [creativeCategory, setCreativeCategory] = useLocalStorage('block-category', 'construction');

  // Component management
  const [components, setComponents] = useLocalStorage<ComponentInstance[]>('block-components-v2', [
    {
      name: 'minecraft:destructible_by_mining',
      enabled: true,
      properties: { seconds_to_destroy: 1.5 },
      metadata: { addedAt: Date.now(), category: 'Core', difficulty: 'beginner' }
    },
    {
      name: 'minecraft:destructible_by_explosion',
      enabled: true,
      properties: { explosion_resistance: 6.0 },
      metadata: { addedAt: Date.now(), category: 'Core', difficulty: 'beginner' }
    },
    {
      name: 'minecraft:material_instances',
      enabled: true,
      properties: {
        "*": {
          texture: "custom_block",
          render_method: "opaque"
        }
      },
      metadata: { addedAt: Date.now(), category: 'Visual', difficulty: 'beginner' }
    }
  ]);

  // UI state
  const [activeTab, setActiveTab] = useState('basic');
  const [selectedComponent, setSelectedComponent] = useState<string | null>(null);
  const [showComponentSelector, setShowComponentSelector] = useState(false);
  const [showComponentForm, setShowComponentForm] = useState(false);
  const [editingComponent, setEditingComponent] = useState<ComponentInstance | null>(null);

  // Convert registry components to ComponentItem format
  const availableComponents: ComponentItem[] = useMemo(() => {
    return blockComponents.map(comp => ({
      name: comp.name,
      description: comp.description,
      category: comp.category,
      subcategory: comp.subcategory,
      version: comp.version,
      difficulty: comp.difficulty,
      properties: comp.properties,
      example: comp.example,
      keywords: comp.keywords,
      stability: comp.stability,
      dependencies: comp.dependencies,
      conflicts: comp.conflicts
    }));
  }, []);

  // Get selected component names for ComponentSelector
  const selectedComponentNames = components.map(c => c.name);

  // Generate JSON
  const blockJSON = useMemo(() => {
    const componentsJSON = generateComponentsJSON(components);

    return {
      format_version: "1.21.0",
      "minecraft:block": {
        description: {
          identifier: identifier || "my_addon:custom_block",
          register_to_creative_menu: registerToCreative,
          ...(registerToCreative && {
            menu_category: {
              category: creativeCategory
            }
          })
        },
        components: componentsJSON
      }
    };
  }, [identifier, registerToCreative, creativeCategory, components]);

  const validation = validateBlockJSON(blockJSON);
  const recommendedComponents = getRecommendedComponents(components, availableComponents, 3);

  // Creative menu categories
  const creativeCategories = [
    { value: 'construction', label: 'Construction' },
    { value: 'nature', label: 'Nature' },
    { value: 'equipment', label: 'Equipment' },
    { value: 'items', label: 'Items' }
  ];

  // Component management functions
  const addComponent = (componentName: string) => {
    const componentDef = availableComponents.find(c => c.name === componentName);
    if (!componentDef) return;

    // Check compatibility
    const compatibility = checkComponentCompatibility(componentDef, components, availableComponents);
    if (!compatibility.compatible) {
      toast({
        title: "Cannot add component",
        description: compatibility.issues.join(', '),
        variant: "destructive"
      });
      return;
    }

    // Generate default properties
    const defaultProperties: Record<string, any> = {};
    componentDef.properties.forEach(prop => {
      if (prop.default !== undefined) {
        defaultProperties[prop.name] = prop.default;
      } else {
        switch (prop.type) {
          case 'number':
            defaultProperties[prop.name] = prop.min ?? prop.example ?? 0;
            break;
          case 'boolean':
            defaultProperties[prop.name] = prop.example ?? false;
            break;
          case 'string':
            defaultProperties[prop.name] = prop.options?.[0] ?? prop.example ?? '';
            break;
          case 'range':
            defaultProperties[prop.name] = prop.example ?? { min: 0, max: 1 };
            break;
          case 'vector3':
            defaultProperties[prop.name] = prop.example ?? { x: 0, y: 0, z: 0 };
            break;
          case 'array':
            defaultProperties[prop.name] = prop.example ?? [];
            break;
          case 'object':
            defaultProperties[prop.name] = prop.example ?? {};
            break;
        }
      }
    });

    const newComponent: ComponentInstance = {
      name: componentName,
      enabled: true,
      properties: defaultProperties,
      metadata: {
        addedAt: Date.now(),
        category: componentDef.category,
        difficulty: componentDef.difficulty
      }
    };

    setComponents([...components, newComponent]);
    setShowComponentSelector(false);

    // Show warnings if any
    if (compatibility.warnings.length > 0) {
      toast({
        title: "Component added with warnings",
        description: compatibility.warnings.join(', '),
        variant: "default"
      });
    }
  };

  const removeComponent = (componentName: string) => {
    setComponents(components.filter(c => c.name !== componentName));
    if (selectedComponent === componentName) {
      setSelectedComponent(null);
    }
  };

  const updateComponent = (componentName: string, newProperties: Record<string, any>) => {
    setComponents(components.map(comp => 
      comp.name === componentName 
        ? { ...comp, properties: newProperties }
        : comp
    ));
  };

  const toggleComponent = (componentName: string) => {
    setComponents(components.map(comp =>
      comp.name === componentName 
        ? { ...comp, enabled: !comp.enabled }
        : comp
    ));
  };

  const openComponentForm = (component: ComponentInstance) => {
    setEditingComponent(component);
    setShowComponentForm(true);
  };

  const handleComponentFormSubmit = (values: Record<string, any>) => {
    if (editingComponent) {
      updateComponent(editingComponent.name, values);
      setShowComponentForm(false);
      setEditingComponent(null);

      toast({
        title: "Component updated",
        description: `${editingComponent.name} has been updated successfully.`
      });
    }
  };

  const applyBlockPreset = (presetName: string) => {
    const presetComponents = applyPreset(presetName, 'block', availableComponents);
    setComponents(presetComponents);

    toast({
      title: "Preset applied",
      description: `Applied ${presetName} preset with ${presetComponents.length} components.`
    });
  };

  const resetBlock = () => {
    setComponents([
      {
        name: 'minecraft:destructible_by_mining',
        enabled: true,
        properties: { seconds_to_destroy: 1.5 },
        metadata: { addedAt: Date.now(), category: 'Core', difficulty: 'beginner' }
      }
    ]);
    setIdentifier('');
    setDisplayName('');
    setDescription('');

    toast({
      title: "Block reset",
      description: "Block has been reset to default state."
    });
  };

  const exportToClipboard = () => {
    navigator.clipboard.writeText(JSON.stringify(blockJSON, null, 2));
    toast({
      title: "Copied to clipboard",
      description: "Block JSON has been copied to your clipboard."
    });
  };

  // Components display in categories
  const renderComponentsByCategory = () => (
    <div className="space-y-3">
      {Object.entries(componentsByCategory).map(([category, categoryComponents]) => (
        <Accordion key={category} type="single" collapsible>
          <AccordionItem value={category}>
            <AccordionTrigger className="text-sm">
              <div className="flex items-center gap-2">
                <Layers className="w-4 h-4" />
                {category}
                <Badge variant="outline">{categoryComponents.length}</Badge>
              </div>
            </AccordionTrigger>
            <AccordionContent>
              <div className="space-y-2 pt-2">
                {categoryComponents.map((component) => {
                  const def = getComponentDefinition(component.name);
                  return (
                    <Card key={component.name} className="p-3">
                      <div className="flex items-start justify-between gap-2">
                        <div className="flex-1 min-w-0">
                          <div className="flex items-center gap-2 mb-1">
                            <Switch
                              checked={component.enabled}
                              onCheckedChange={() => toggleComponent(component.name)}
                              data-testid={`switch-${component.name}`}
                            />
                            <span className="font-medium text-sm truncate">
                              {component.name}
                            </span>
                            {def && (
                              <Badge 
                                variant="outline" 
                                className={`text-xs ${
                                  def.difficulty === 'beginner' ? 'border-green-500' :
                                  def.difficulty === 'intermediate' ? 'border-yellow-500' :
                                  'border-red-500'
                                }`}
                              >
                                {def.difficulty}
                              </Badge>
                            )}
                          </div>
                          {def && (
                            <p className="text-xs text-muted-foreground line-clamp-2">
                              {def.description}
                            </p>
                          )}
                        </div>
                        <div className="flex gap-1">
                          <Tooltip>
                            <TooltipTrigger asChild>
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => openComponentForm(component)}
                                disabled={!component.enabled}
                                data-testid={`button-configure-${component.name}`}
                              >
                                <Zap className="w-3 h-3" />
                              </Button>
                            </TooltipTrigger>
                            <TooltipContent>Configure properties</TooltipContent>
                          </Tooltip>
                          <Tooltip>
                            <TooltipTrigger asChild>
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => removeComponent(component.name)}
                                data-testid={`button-remove-${component.name}`}
                              >
                                <AlertCircle className="w-3 h-3" />
                              </Button>
                            </TooltipTrigger>
                            <TooltipContent>Remove component</TooltipContent>
                          </Tooltip>
                        </div>
                      </div>
                    </Card>
                  );
                })}
              </div>
            </AccordionContent>
          </AccordionItem>
        </Accordion>
      ))}
    </div>
  );

  // Render tab badge counts
  const getComponentsTabLabel = () => `Components${components.length > 0 ? ` (${components.length})` : ''}`;
  const getAdvancedTabLabel = () => {
    const errorCount = validation.errors?.length || 0;
    return errorCount > 0 ? `Advanced (${errorCount})` : 'Advanced';
  };

  const componentsByCategory = useMemo(() => {
    const grouped: Record<string, ComponentInstance[]> = {};
    components.forEach(comp => {
      const category = comp.metadata?.category || 'Other';
      if (!grouped[category]) grouped[category] = [];
      grouped[category].push(comp);
    });
    return grouped;
  }, [components]);

  const getComponentDefinition = (componentName: string): ComponentDefinition | null => {
    const def = availableComponents.find(c => c.name === componentName);
    if (!def) return null;

    return {
      name: def.name,
      description: def.description,
      category: def.category,
      subcategory: def.subcategory,
      version: def.version,
      difficulty: def.difficulty,
      properties: def.properties,
      example: def.example,
      keywords: def.keywords,
      stability: def.stability,
      dependencies: def.dependencies,
      conflicts: def.conflicts
    };
  };

  return (
    <TooltipProvider>
      <section className="p-6" data-testid="block-builder">
        <div className="max-w-7xl mx-auto">
          <div className="grid lg:grid-cols-2 gap-6">
            {/* Builder Form */}
            <div className="space-y-6">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Package className="w-5 h-5" />
                    Block Configuration
                  </CardTitle>
                  <CardDescription>
                    Create comprehensive Minecraft Bedrock blocks with all official components
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <Tabs value={activeTab} onValueChange={setActiveTab}>
                    <TabsList className="grid w-full grid-cols-4">
                      <TabsTrigger value="basic" data-testid="tab-basic">Basic</TabsTrigger>
                      <TabsTrigger value="components" data-testid="tab-components">
                        {getComponentsTabLabel()}
                      </TabsTrigger>
                      <TabsTrigger value="presets" data-testid="tab-presets">Presets</TabsTrigger>
                      <TabsTrigger value="advanced" data-testid="tab-advanced">
                        {getAdvancedTabLabel()}
                      </TabsTrigger>
                    </TabsList>

                    <TabsContent value="basic" className="space-y-4">
                      <div className="grid gap-4">
                        <div>
                          <Label htmlFor="block-identifier">Block Identifier *</Label>
                          <Input
                            id="block-identifier"
                            value={identifier}
                            onChange={(e) => setIdentifier(e.target.value)}
                            placeholder="my_addon:custom_block"
                            data-testid="input-identifier"
                          />
                          <p className="text-xs text-muted-foreground mt-1">
                            Must include namespace (e.g., my_addon:block_name)
                          </p>
                        </div>

                        <div>
                          <Label htmlFor="block-display-name">Display Name</Label>
                          <Input
                            id="block-display-name"
                            value={displayName}
                            onChange={(e) => setDisplayName(e.target.value)}
                            placeholder="Custom Block"
                            data-testid="input-display-name"
                          />
                        </div>

                        <div>
                          <Label htmlFor="block-description">Description</Label>
                          <Input
                            id="block-description"
                            value={description}
                            onChange={(e) => setDescription(e.target.value)}
                            placeholder="A custom block for my addon"
                            data-testid="input-description"
                          />
                        </div>

                        <div className="space-y-3">
                          <div className="flex items-center space-x-2">
                            <Switch
                              id="register-creative"
                              checked={registerToCreative}
                              onCheckedChange={setRegisterToCreative}
                              data-testid="switch-creative"
                            />
                            <Label htmlFor="register-creative">Register to Creative Menu</Label>
                          </div>

                          {registerToCreative && (
                            <div>
                              <Label htmlFor="creative-category">Creative Category</Label>
                              <Select value={creativeCategory} onValueChange={setCreativeCategory}>
                                <SelectTrigger data-testid="select-creative-category">
                                  <SelectValue />
                                </SelectTrigger>
                                <SelectContent>
                                  {creativeCategories.map(category => (
                                    <SelectItem key={category.value} value={category.value}>
                                      {category.label}
                                    </SelectItem>
                                  ))}
                                </SelectContent>
                              </Select>
                            </div>
                          )}
                        </div>
                      </div>
                    </TabsContent>

                    <TabsContent value="components" className="space-y-4">
                      <div className="flex items-center justify-between">
                        <h4 className="font-medium">Block Components</h4>
                        <Button 
                          onClick={() => setShowComponentSelector(true)}
                          data-testid="button-add-component"
                        >
                          Add Component
                        </Button>
                      </div>

                      {recommendedComponents.length > 0 && (
                        <Alert>
                          <Info className="h-4 w-4" />
                          <AlertDescription>
                            <div className="space-y-2">
                              <p className="font-medium">Recommended components:</p>
                              <div className="flex flex-wrap gap-1">
                                {recommendedComponents.slice(0, 3).map(comp => (
                                  <Button
                                    key={comp.name}
                                    variant="outline"
                                    size="sm"
                                    onClick={() => addComponent(comp.name)}
                                    data-testid={`button-add-recommended-${comp.name}`}
                                  >
                                    {comp.name}
                                  </Button>
                                ))}
                              </div>
                            </div>
                          </AlertDescription>
                        </Alert>
                      )}

                      <ScrollArea className="h-[400px]">
                        {renderComponentsByCategory()}
                      </ScrollArea>
                    </TabsContent>

                    <TabsContent value="presets" className="space-y-4">
                      <div>
                        <h4 className="font-medium mb-3">Block Presets</h4>
                        <p className="text-sm text-muted-foreground mb-4">
                          Quick start with common block configurations
                        </p>
                        <div className="grid gap-3">
                          {Object.entries(BLOCK_PRESETS).map(([presetName, componentList]) => (
                            <Card key={presetName} className="p-4 cursor-pointer hover:bg-muted/50">
                              <div className="flex items-center justify-between">
                                <div>
                                  <h5 className="font-medium capitalize">
                                    {presetName.replace(/_/g, ' ')}
                                  </h5>
                                  <p className="text-xs text-muted-foreground">
                                    {componentList.length} components
                                  </p>
                                </div>
                                <Button
                                  variant="outline"
                                  size="sm"
                                  onClick={() => applyBlockPreset(presetName)}
                                  data-testid={`button-preset-${presetName}`}
                                >
                                  Apply
                                </Button>
                              </div>
                            </Card>
                          ))}
                        </div>
                      </div>
                    </TabsContent>

                    <TabsContent value="advanced" className="space-y-4">
                      <div className="space-y-4">
                        <div>
                          <h4 className="font-medium mb-3">Block Actions</h4>
                          <div className="flex gap-2 mb-3">
                            <Button
                              variant="outline"
                              onClick={resetBlock}
                              data-testid="button-reset"
                            >
                              <RotateCcw className="w-4 h-4 mr-2" />
                              Reset Block
                            </Button>
                            <Button
                              variant="outline"
                              onClick={exportToClipboard}
                              data-testid="button-export"
                            >
                              <Copy className="w-4 h-4 mr-2" />
                              Copy JSON
                            </Button>
                          </div>
                          <p className="text-xs text-muted-foreground">
                            Reset will clear all components and properties. Copy JSON exports the current block configuration.
                          </p>
                        </div>

                        <div>
                          <h4 className="font-medium mb-3">Validation Status</h4>
                          <ValidationStatus validation={validation} />
                        </div>

                        <div>
                          <h4 className="font-medium mb-3">Export Options</h4>
                          <div className="grid grid-cols-2 gap-2 mb-3">
                            <Button variant="outline" size="sm">
                              <Download className="w-3 h-3 mr-2" />
                              Download .json
                            </Button>
                            <Button variant="outline" size="sm">
                              <Save className="w-3 h-3 mr-2" />
                              Save Template
                            </Button>
                          </div>
                          <p className="text-xs text-muted-foreground">
                            Export your block as a .json file or save as a reusable template.
                          </p>
                        </div>
                      </div>
                    </TabsContent>
                  </Tabs>
                </CardContent>
              </Card>
            </div>

            {/* JSON Preview */}
            <div className="space-y-6">
              <CodePreview 
                code={JSON.stringify(blockJSON, null, 2)}
                language="json"
                title="Block JSON"
              />
            </div>
          </div>
        </div>

        {/* Component Selector Modal */}
        {showComponentSelector && (
          <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
            <div className="bg-background rounded-lg shadow-lg max-w-4xl w-full max-h-[90vh] overflow-hidden">
              <ComponentSelector
                components={availableComponents}
                selectedComponents={selectedComponentNames}
                onAddComponent={addComponent}
                onRemoveComponent={removeComponent}
                title="Add Block Components"
                description="Choose from 25+ official Minecraft Bedrock block components"
                showCategories={true}
                showCompatibility={true}
              />
              <div className="p-4 border-t flex justify-end">
                <Button 
                  variant="outline" 
                  onClick={() => setShowComponentSelector(false)}
                  data-testid="button-close-selector"
                >
                  Close
                </Button>
              </div>
            </div>
          </div>
        )}

        {/* Component Form Modal */}
        {showComponentForm && editingComponent && (
          <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
            <div className="bg-background rounded-lg shadow-lg max-w-2xl w-full max-h-[90vh] overflow-hidden">
              {(() => {
                const def = getComponentDefinition(editingComponent.name);
                return def ? (
                  <ComponentForm
                    component={def}
                    initialValues={editingComponent.properties}
                    onSubmit={handleComponentFormSubmit}
                    onCancel={() => {
                      setShowComponentForm(false);
                      setEditingComponent(null);
                    }}
                    isEditing={true}
                    showExample={true}
                  />
                ) : null;
              })()}
            </div>
          </div>
        )}
      </section>
    </TooltipProvider>
  );
}

client/src/components/Builders/EntityBui.content:
import { useState, useMemo, useEffect } from "react";
import { Sparkles, Download, Copy, Save, RotateCcw, Info, Zap, AlertCircle, X, Settings, FileText, Layers } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Accordion, AccordionContent, AccordionItem, AccordionTrigger } from "@/components/ui/accordion";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "@/components/ui/tooltip";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";

import CodePreview from "@/components/Common/CodePreview";
import ValidationStatus from "@/components/Common/ValidationStatus";
import ComponentSelector, { ComponentItem } from "@/components/Common/ComponentSelector";
import ComponentForm, { ComponentDefinition } from "@/components/Common/ComponentForm";

import { useLocalStorage } from "@/hooks/useLocalStorage";
import { useToast } from "@/hooks/use-toast";
import { validateEntityJSON } from "@/lib/minecraft/validation";
import { 
  ComponentInstance, 
  generateComponentsJSON, 
  applyPreset, 
  getRecommendedComponents,
  checkComponentCompatibility,
  validateComponentProperties,
  ENTITY_PRESETS 
} from "@/lib/minecraft/componentUtils";

// Import the comprehensive entity registry
import { entityComponents } from "@shared/entityRegistry";
import ComponentIndex from "@shared/componentIndex";

export default function EntityBuilder() {
  const { toast } = useToast();

  // Basic entity properties
  const [identifier, setIdentifier] = useLocalStorage('entity-identifier', '');
  const [displayName, setDisplayName] = useLocalStorage('entity-display-name', '');
  const [description, setDescription] = useLocalStorage('entity-description', '');
  const [isSpawnable, setIsSpawnable] = useLocalStorage('entity-spawnable', true);
  const [isSummonable, setIsSummonable] = useLocalStorage('entity-summonable', true);
  const [isExperimental, setIsExperimental] = useLocalStorage('entity-experimental', false);

  // Component management
  const [components, setComponents] = useLocalStorage<ComponentInstance[]>('entity-components-v2', [
    {
      name: 'minecraft:health',
      enabled: true,
      properties: { value: 20, max: 20 },
      metadata: { addedAt: Date.now(), category: 'Core', difficulty: 'beginner' }
    },
    {
      name: 'minecraft:physics',
      enabled: true,
      properties: {},
      metadata: { addedAt: Date.now(), category: 'Core', difficulty: 'beginner' }
    }
  ]);

  // UI state
  const [activeTab, setActiveTab] = useState('basic');
  const [selectedComponent, setSelectedComponent] = useState<string | null>(null);
  const [showComponentSelector, setShowComponentSelector] = useState(false);
  const [showComponentForm, setShowComponentForm] = useState(false);
  const [editingComponent, setEditingComponent] = useState<ComponentInstance | null>(null);

  // Group current components by category
  const componentsByCategory = useMemo(() => {
    const grouped: Record<string, ComponentInstance[]> = {};
    components.forEach(comp => {
      const category = comp.metadata?.category || 'Other';
      if (!grouped[category]) grouped[category] = [];
      grouped[category].push(comp);
    });
    return grouped;
  }, [components]);

  // Convert registry components to ComponentItem format
  const availableComponents: ComponentItem[] = useMemo(() => {
    return entityComponents.map(comp => ({
      name: comp.name,
      description: comp.description,
      category: comp.category,
      subcategory: comp.subcategory,
      version: comp.version,
      difficulty: comp.difficulty,
      properties: comp.properties,
      example: comp.example,
      keywords: comp.keywords,
      stability: comp.stability,
      dependencies: comp.dependencies,
      conflicts: comp.conflicts
    }));
  }, []);


  // Get selected component names for ComponentSelector
  const selectedComponentNames = components.map(c => c.name);

  // Generate JSON
  const entityJSON = useMemo(() => {
    const componentsJSON = generateComponentsJSON(components);

    return {
      format_version: "1.21.0",
      "minecraft:entity": {
        description: {
          identifier: identifier || "my_addon:custom_entity",
          is_spawnable: isSpawnable,
          is_summonable: isSummonable,
          is_experimental: isExperimental
        },
        component_groups: {},
        components: {
          "minecraft:type_family": {
            family: ["custom", "mob"]
          },
          "minecraft:collision_box": {
            width: 0.6,
            height: 1.8
          },
          ...componentsJSON
        },
        events: {}
      }
    };
  }, [identifier, isSpawnable, isSummonable, isExperimental, components]);

  const validation = validateEntityJSON(entityJSON);
  const recommendedComponents = getRecommendedComponents(components, availableComponents);

  // Component management functions
  const addComponent = (componentName: string) => {
    const componentDef = availableComponents.find(c => c.name === componentName);
    if (!componentDef) return;

    // Check compatibility
    const compatibility = checkComponentCompatibility(componentDef, components, availableComponents);
    if (!compatibility.compatible) {
      toast({
        title: "Cannot add component",
        description: compatibility.issues.join(', '),
        variant: "destructive"
      });
      return;
    }

    // Generate default properties
    const defaultProperties: Record<string, any> = {};
    componentDef.properties.forEach(prop => {
      if (prop.default !== undefined) {
        defaultProperties[prop.name] = prop.default;
      } else {
        switch (prop.type) {
          case 'number':
            defaultProperties[prop.name] = prop.min ?? prop.example ?? 0;
            break;
          case 'boolean':
            defaultProperties[prop.name] = prop.example ?? false;
            break;
          case 'string':
            defaultProperties[prop.name] = prop.options?.[0] ?? prop.example ?? '';
            break;
          case 'range':
            defaultProperties[prop.name] = prop.example ?? { min: 0, max: 1 };
            break;
          case 'vector3':
            defaultProperties[prop.name] = prop.example ?? { x: 0, y: 0, z: 0 };
            break;
          case 'array':
            defaultProperties[prop.name] = prop.example ?? [];
            break;
          case 'object':
            defaultProperties[prop.name] = prop.example ?? {};
            break;
        }
      }
    });

    const newComponent: ComponentInstance = {
      name: componentName,
      enabled: true,
      properties: defaultProperties,
      metadata: {
        addedAt: Date.now(),
        category: componentDef.category,
        difficulty: componentDef.difficulty
      }
    };

    setComponents([...components, newComponent]);
    setShowComponentSelector(false);

    // Show warnings if any
    if (compatibility.warnings.length > 0) {
      toast({
        title: "Component added with warnings",
        description: compatibility.warnings.join(', '),
        variant: "default"
      });
    }
  };

  const removeComponent = (componentName: string) => {
    setComponents(components.filter(c => c.name !== componentName));
    if (selectedComponent === componentName) {
      setSelectedComponent(null);
    }
  };

  const updateComponent = (componentName: string, newProperties: Record<string, any>) => {
    setComponents(components.map(comp => 
      comp.name === componentName 
        ? { ...comp, properties: newProperties }
        : comp
    ));
  };

  const toggleComponent = (componentName: string) => {
    setComponents(components.map(comp =>
      comp.name === componentName 
        ? { ...comp, enabled: !comp.enabled }
        : comp
    ));
  };

  const openComponentForm = (component: ComponentInstance) => {
    setEditingComponent(component);
    setShowComponentForm(true);
  };

  const handleComponentFormSubmit = (values: Record<string, any>) => {
    if (editingComponent) {
      updateComponent(editingComponent.name, values);
      setShowComponentForm(false);
      setEditingComponent(null);

      toast({
        title: "Component updated",
        description: `${editingComponent.name} has been updated successfully.`
      });
    }
  };

  const applyEntityPreset = (presetName: string) => {
    const presetComponents = applyPreset(presetName, 'entity', availableComponents);
    setComponents(presetComponents);

    toast({
      title: "Preset applied",
      description: `Applied ${presetName} preset with ${presetComponents.length} components.`
    });
  };

  const resetEntity = () => {
    setComponents([
      {
        name: 'minecraft:health',
        enabled: true,
        properties: { value: 20, max: 20 },
        metadata: { addedAt: Date.now(), category: 'Core', difficulty: 'beginner' }
      }
    ]);
    setIdentifier('');
    setDisplayName('');
    setDescription('');

    toast({
      title: "Entity reset",
      description: "Entity has been reset to default state."
    });
  };

  const exportToClipboard = () => {
    navigator.clipboard.writeText(JSON.stringify(entityJSON, null, 2));
    toast({
      title: "Copied to clipboard",
      description: "Entity JSON has been copied to your clipboard."
    });
  };


  const getComponentDefinition = (componentName: string): ComponentDefinition | null => {
    const def = availableComponents.find(c => c.name === componentName);
    if (!def) return null;

    return {
      name: def.name,
      description: def.description,
      category: def.category,
      subcategory: def.subcategory,
      version: def.version,
      difficulty: def.difficulty,
      properties: def.properties,
      example: def.example,
      keywords: def.keywords,
      stability: def.stability,
      dependencies: def.dependencies,
      conflicts: def.conflicts
    };
  };

  // Tab label functions
  const getComponentsTabLabel = () => {
    const enabledCount = components.filter(c => c.enabled).length;
    return `Components (${enabledCount})`;
  };

  const getAdvancedTabLabel = () => "Advanced";

  // Components display in categories
  const renderComponentsByCategory = () => (
    <div className="space-y-3">
      {Object.entries(componentsByCategory).map(([category, categoryComponents]) => (
        <Accordion key={category} type="single" collapsible>
          <AccordionItem value={category}>
            <AccordionTrigger className="text-sm">
              <div className="flex items-center gap-2">
                <Layers className="w-4 h-4" />
                {category}
                <Badge variant="outline">{categoryComponents.length}</Badge>
              </div>
            </AccordionTrigger>
            <AccordionContent>
              <div className="space-y-2 pt-2">
                {categoryComponents.map((component) => {
                  const def = getComponentDefinition(component.name);
                  return (
                    <Card key={component.name} className="p-3">
                      <div className="flex items-start justify-between gap-2">
                        <div className="flex-1 min-w-0">
                          <div className="flex items-center gap-2 mb-1">
                            <Switch
                              checked={component.enabled}
                              onCheckedChange={() => toggleComponent(component.name)}
                              data-testid={`switch-${component.name}`}
                            />
                            <span className="font-medium text-sm truncate">
                              {component.name}
                            </span>
                            {def && (
                              <Badge 
                                variant="outline" 
                                className={`text-xs ${
                                  def.difficulty === 'beginner' ? 'border-green-500' :
                                  def.difficulty === 'intermediate' ? 'border-yellow-500' :
                                  'border-red-500'
                                }`}
                              >
                                {def.difficulty}
                              </Badge>
                            )}
                          </div>
                          {def && (
                            <p className="text-xs text-muted-foreground line-clamp-2">
                              {def.description}
                            </p>
                          )}
                        </div>
                        <div className="flex gap-1">
                          <Tooltip>
                            <TooltipTrigger asChild>
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => openComponentForm(component)}
                                disabled={!component.enabled}
                                data-testid={`button-configure-${component.name}`}
                              >
                                <Zap className="w-3 h-3" />
                              </Button>
                            </TooltipTrigger>
                            <TooltipContent>Configure properties</TooltipContent>
                          </Tooltip>
                          <Tooltip>
                            <TooltipTrigger asChild>
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => removeComponent(component.name)}
                                data-testid={`button-remove-${component.name}`}
                              >
                                <AlertCircle className="w-3 h-3" />
                              </Button>
                            </TooltipTrigger>
                            <TooltipContent>Remove component</TooltipContent>
                          </Tooltip>
                        </div>
                      </div>
                    </Card>
                  );
                })}
              </div>
            </AccordionContent>
          </AccordionItem>
        </Accordion>
      ))}
    </div>
  );


  return (
    <TooltipProvider>
      <section className="p-6" data-testid="entity-builder">
        <div className="max-w-7xl mx-auto">
          <div className="grid lg:grid-cols-2 gap-6">
            {/* Builder Form */}
            <div className="space-y-6">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Sparkles className="w-5 h-5" />
                    Entity Configuration
                  </CardTitle>
                  <CardDescription>
                    Create comprehensive Minecraft Bedrock entities with all official components
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <Tabs value={activeTab} onValueChange={setActiveTab}>
                    <TabsList className="grid w-full grid-cols-4">
                      <TabsTrigger value="basic" data-testid="tab-basic">Basic</TabsTrigger>
                      <TabsTrigger value="components" data-testid="tab-components">
                        {getComponentsTabLabel()}
                      </TabsTrigger>
                      <TabsTrigger value="presets" data-testid="tab-presets">Presets</TabsTrigger>
                      <TabsTrigger value="advanced" data-testid="tab-advanced">
                        {getAdvancedTabLabel()}
                      </TabsTrigger>
                    </TabsList>

                    <TabsContent value="basic" className="space-y-4">
                      <div className="grid gap-4">
                        <div>
                          <Label htmlFor="entity-identifier">Entity Identifier *</Label>
                          <Input
                            id="entity-identifier"
                            value={identifier}
                            onChange={(e) => setIdentifier(e.target.value)}
                            placeholder="my_addon:custom_entity"
                            data-testid="input-identifier"
                          />
                          <p className="text-xs text-muted-foreground mt-1">
                            Must include namespace (e.g., my_addon:entity_name)
                          </p>
                        </div>

                        <div>
                          <Label htmlFor="entity-display-name">Display Name</Label>
                          <Input
                            id="entity-display-name"
                            value={displayName}
                            onChange={(e) => setDisplayName(e.target.value)}
                            placeholder="Custom Entity"
                            data-testid="input-display-name"
                          />
                        </div>

                        <div>
                          <Label htmlFor="entity-description">Description</Label>
                          <Input
                            id="entity-description"
                            value={description}
                            onChange={(e) => setDescription(e.target.value)}
                            placeholder="A custom entity for my addon"
                            data-testid="input-description"
                          />
                        </div>

                        <div className="space-y-3">
                          <div className="flex items-center space-x-2">
                            <Switch
                              id="is-spawnable"
                              checked={isSpawnable}
                              onCheckedChange={setIsSpawnable}
                              data-testid="switch-spawnable"
                            />
                            <Label htmlFor="is-spawnable">Spawnable</Label>
                          </div>

                          <div className="flex items-center space-x-2">
                            <Switch
                              id="is-summonable"
                              checked={isSummonable}
                              onCheckedChange={setIsSummonable}
                              data-testid="switch-summonable"
                            />
                            <Label htmlFor="is-summonable">Summonable</Label>
                          </div>

                          <div className="flex items-center space-x-2">
                            <Switch
                              id="is-experimental"
                              checked={isExperimental}
                              onCheckedChange={setIsExperimental}
                              data-testid="switch-experimental"
                            />
                            <Label htmlFor="is-experimental">Experimental</Label>
                          </div>
                        </div>
                      </div>
                    </TabsContent>

                    <TabsContent value="components" className="space-y-4">
                      <div className="flex items-center justify-between">
                        <h4 className="font-medium">Entity Components</h4>
                        <Button 
                          onClick={() => setShowComponentSelector(true)}
                          data-testid="button-add-component"
                        >
                          Add Component
                        </Button>
                      </div>

                      {recommendedComponents.length > 0 && (
                        <Alert>
                          <Info className="h-4 w-4" />
                          <AlertDescription>
                            <div className="space-y-2">
                              <p className="font-medium">Recommended components:</p>
                              <div className="flex flex-wrap gap-1">
                                {recommendedComponents.slice(0, 3).map(comp => (
                                  <Button
                                    key={comp.name}
                                    variant="outline"
                                    size="sm"
                                    onClick={() => addComponent(comp.name)}
                                    data-testid={`button-add-recommended-${comp.name}`}
                                  >
                                    {comp.name}
                                  </Button>
                                ))}
                              </div>
                            </div>
                          </AlertDescription>
                        </Alert>
                      )}

                      <ScrollArea className="h-[400px]">
                        {renderComponentsByCategory()}
                      </ScrollArea>
                    </TabsContent>

                    <TabsContent value="presets" className="space-y-4">
                      <div>
                        <h4 className="font-medium mb-3">Entity Presets</h4>
                        <p className="text-sm text-muted-foreground mb-4">
                          Quick start with common entity configurations
                        </p>
                        <div className="grid gap-3">
                          {Object.entries(ENTITY_PRESETS).map(([presetName, componentList]) => (
                            <Card key={presetName} className="p-4 cursor-pointer hover:bg-muted/50">
                              <div className="flex items-center justify-between">
                                <div>
                                  <h5 className="font-medium capitalize">
                                    {presetName.replace(/_/g, ' ')}
                                  </h5>
                                  <p className="text-xs text-muted-foreground">
                                    {componentList.length} components
                                  </p>
                                </div>
                                <Button
                                  variant="outline"
                                  size="sm"
                                  onClick={() => applyEntityPreset(presetName)}
                                  data-testid={`button-preset-${presetName}`}
                                >
                                  Apply
                                </Button>
                              </div>
                            </Card>
                          ))}
                        </div>
                      </div>
                    </TabsContent>

                    <TabsContent value="advanced" className="space-y-4">
                      <div className="space-y-4">
                        <div>
                          <h4 className="font-medium mb-3">Entity Actions</h4>
                          <div className="flex gap-2 mb-3">
                            <Button
                              variant="outline"
                              onClick={resetEntity}
                              data-testid="button-reset"
                            >
                              <RotateCcw className="w-4 h-4 mr-2" />
                              Reset Entity
                            </Button>
                            <Button
                              variant="outline"
                              onClick={exportToClipboard}
                              data-testid="button-export"
                            >
                              <Copy className="w-4 h-4 mr-2" />
                              Copy JSON
                            </Button>
                          </div>
                          <p className="text-xs text-muted-foreground">
                            Reset will clear all components and properties. Copy JSON exports the current entity configuration.
                          </p>
                        </div>

                        <div>
                          <h4 className="font-medium mb-3">Validation Status</h4>
                          <ValidationStatus validation={validation} />
                        </div>

                        <div>
                          <h4 className="font-medium mb-3">Export Options</h4>
                          <div className="grid grid-cols-2 gap-2 mb-3">
                            <Button variant="outline" size="sm">
                              <Download className="w-3 h-3 mr-2" />
                              Download .json
                            </Button>
                            <Button variant="outline" size="sm">
                              <Save className="w-3 h-3 mr-2" />
                              Save Template
                            </Button>
                          </div>
                          <p className="text-xs text-muted-foreground">
                            Export your entity as a .json file or save as a reusable template.
                          </p>
                        </div>
                      </div>
                    </TabsContent>
                  </Tabs>
                </CardContent>
              </Card>
            </div>

            {/* JSON Preview */}
            <div className="space-y-6">
              <CodePreview 
                code={JSON.stringify(entityJSON, null, 2)}
                language="json"
                title="Entity JSON"
              />
            </div>
          </div>
        </div>

        {/* Component Selector Modal */}
        <Dialog open={showComponentSelector} onOpenChange={setShowComponentSelector}>
          <DialogContent className="max-w-6xl max-h-[90vh] overflow-hidden" data-testid="component-selector-modal">
            <DialogHeader>
              <DialogTitle className="flex items-center justify-between">
                Add Entity Components
                <Button 
                  variant="ghost" 
                  size="sm" 
                  onClick={() => setShowComponentSelector(false)}
                  data-testid="button-close-selector-x"
                >
                  <X className="w-4 h-4" />
                </Button>
              </DialogTitle>
              <DialogDescription>
                Choose from 50+ official Minecraft Bedrock entity components
              </DialogDescription>
            </DialogHeader>
            <ScrollArea className="max-h-[70vh]">
              <ComponentSelector
                components={availableComponents}
                selectedComponents={selectedComponentNames}
                onAddComponent={addComponent}
                onRemoveComponent={removeComponent}
                title="Add Entity Components"
                description="Choose from 50+ official Minecraft Bedrock entity components"
                showCategories={true}
                showCompatibility={true}
              />
            </ScrollArea>
            <div className="flex justify-end gap-2 pt-4 border-t">
              <Button 
                variant="outline" 
                onClick={() => setShowComponentSelector(false)}
                data-testid="button-close-selector"
              >
                Cancel
              </Button>
            </div>
          </DialogContent>
        </Dialog>

        {/* Component Form Modal */}
        <Dialog open={showComponentForm && !!editingComponent} onOpenChange={(open) => {
          if (!open) {
            setShowComponentForm(false);
            setEditingComponent(null);
          }
        }}>
          <DialogContent className="max-w-4xl max-h-[95vh] overflow-hidden" data-testid="component-form-modal">
            <DialogHeader>
              <DialogTitle className="flex items-center justify-between">
                Configure Component: {editingComponent?.name}
                <Button 
                  variant="ghost" 
                  size="sm" 
                  onClick={() => {
                    setShowComponentForm(false);
                    setEditingComponent(null);
                  }}
                  data-testid="button-close-form-x"
                >
                  <X className="w-4 h-4" />
                </Button>
              </DialogTitle>
              <DialogDescription>
                Configure the properties for this component
              </DialogDescription>
            </DialogHeader>
            <ScrollArea className="max-h-[75vh] pr-4">
              {(() => {
                if (!editingComponent) return null;
                const def = getComponentDefinition(editingComponent.name);
                return def ? (
                  <ComponentForm
                    component={def}
                    initialValues={editingComponent.properties}
                    onSubmit={handleComponentFormSubmit}
                    onCancel={() => {
                      setShowComponentForm(false);
                      setEditingComponent(null);
                    }}
                    isEditing={true}
                    showExample={true}
                  />
                ) : null;
              })()}
            </ScrollArea>
          </DialogContent>
        </Dialog>
      </section>
    </TooltipProvider>
  );
}

client/src/components/Builders/ItemBuild.content:
import { useState, useMemo } from "react";
import { Package, Download, Copy, Save, RotateCcw, Info, Zap, AlertCircle, Settings, FileText, Layers, Box, Cog } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Accordion, AccordionContent, AccordionItem, AccordionTrigger } from "@/components/ui/accordion";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "@/components/ui/tooltip";

import CodePreview from "@/components/Common/CodePreview";
import ValidationStatus from "@/components/Common/ValidationStatus";
import ComponentSelector, { ComponentItem } from "@/components/Common/ComponentSelector";
import ComponentForm, { ComponentDefinition } from "@/components/Common/ComponentForm";

import { useLocalStorage } from "@/hooks/useLocalStorage";
import { useToast } from "@/hooks/use-toast";
import { validateItemJSON } from "@/lib/minecraft/validation";
import { 
  ComponentInstance, 
  generateComponentsJSON, 
  applyPreset, 
  getRecommendedComponents,
  checkComponentCompatibility,
  validateComponentProperties,
  ITEM_PRESETS 
} from "@/lib/minecraft/componentUtils";

// Import the comprehensive item registry
import { itemComponents } from "@shared/itemRegistry";

export default function ItemBuilder() {
  const { toast } = useToast();

  // Basic item properties
  const [identifier, setIdentifier] = useLocalStorage('item-identifier', '');
  const [displayName, setDisplayName] = useLocalStorage('item-display-name', '');
  const [description, setDescription] = useLocalStorage('item-description', '');
  const [menuCategory, setMenuCategory] = useLocalStorage('item-menu-category', 'items');

  // Component management
  const [components, setComponents] = useLocalStorage<ComponentInstance[]>('item-components-v2', [
    {
      name: 'minecraft:max_stack_size',
      enabled: true,
      properties: { value: 64 },
      metadata: { addedAt: Date.now(), category: 'Core', difficulty: 'beginner' }
    },
    {
      name: 'minecraft:icon',
      enabled: true,
      properties: { texture: "custom_item" },
      metadata: { addedAt: Date.now(), category: 'Visual', difficulty: 'beginner' }
    }
  ]);

  // UI state
  const [activeTab, setActiveTab] = useState('basic');
  const [selectedComponent, setSelectedComponent] = useState<string | null>(null);
  const [showComponentSelector, setShowComponentSelector] = useState(false);
  const [showComponentForm, setShowComponentForm] = useState(false);
  const [editingComponent, setEditingComponent] = useState<ComponentInstance | null>(null);

  // Convert registry components to ComponentItem format
  const availableComponents: ComponentItem[] = useMemo(() => {
    return itemComponents.map(comp => ({
      name: comp.name,
      description: comp.description,
      category: comp.category,
      subcategory: comp.subcategory,
      version: comp.version,
      difficulty: comp.difficulty,
      properties: comp.properties,
      example: comp.example,
      keywords: comp.keywords,
      stability: comp.stability,
      dependencies: comp.dependencies,
      conflicts: comp.conflicts
    }));
  }, []);

  // Get selected component names for ComponentSelector
  const selectedComponentNames = components.map(c => c.name);

  // Generate JSON
  const itemJSON = useMemo(() => {
    const componentsJSON = generateComponentsJSON(components);

    return {
      format_version: "1.21.0",
      "minecraft:item": {
        description: {
          identifier: identifier || "my_addon:custom_item",
          menu_category: {
            category: menuCategory
          }
        },
        components: componentsJSON
      }
    };
  }, [identifier, menuCategory, components]);

  const validation = validateItemJSON(itemJSON);
  const recommendedComponents = getRecommendedComponents(components, availableComponents, 3);

  // Menu categories
  const menuCategories = [
    { value: 'items', label: 'Items' },
    { value: 'equipment', label: 'Equipment' },
    { value: 'construction', label: 'Construction' },
    { value: 'nature', label: 'Nature' }
  ];

  // Component management functions
  const addComponent = (componentName: string) => {
    const componentDef = availableComponents.find(c => c.name === componentName);
    if (!componentDef) return;

    // Check compatibility
    const compatibility = checkComponentCompatibility(componentDef, components, availableComponents);
    if (!compatibility.compatible) {
      toast({
        title: "Cannot add component",
        description: compatibility.issues.join(', '),
        variant: "destructive"
      });
      return;
    }

    // Generate default properties
    const defaultProperties: Record<string, any> = {};
    componentDef.properties.forEach(prop => {
      if (prop.default !== undefined) {
        defaultProperties[prop.name] = prop.default;
      } else {
        switch (prop.type) {
          case 'number':
            defaultProperties[prop.name] = prop.min ?? prop.example ?? 0;
            break;
          case 'boolean':
            defaultProperties[prop.name] = prop.example ?? false;
            break;
          case 'string':
            defaultProperties[prop.name] = prop.options?.[0] ?? prop.example ?? '';
            break;
          case 'range':
            defaultProperties[prop.name] = prop.example ?? { min: 0, max: 1 };
            break;
          case 'vector3':
            defaultProperties[prop.name] = prop.example ?? { x: 0, y: 0, z: 0 };
            break;
          case 'array':
            defaultProperties[prop.name] = prop.example ?? [];
            break;
          case 'object':
            defaultProperties[prop.name] = prop.example ?? {};
            break;
        }
      }
    });

    const newComponent: ComponentInstance = {
      name: componentName,
      enabled: true,
      properties: defaultProperties,
      metadata: {
        addedAt: Date.now(),
        category: componentDef.category,
        difficulty: componentDef.difficulty
      }
    };

    setComponents([...components, newComponent]);
    setShowComponentSelector(false);

    // Show warnings if any
    if (compatibility.warnings.length > 0) {
      toast({
        title: "Component added with warnings",
        description: compatibility.warnings.join(', '),
        variant: "default"
      });
    }
  };

  const removeComponent = (componentName: string) => {
    setComponents(components.filter(c => c.name !== componentName));
    if (selectedComponent === componentName) {
      setSelectedComponent(null);
    }
  };

  const updateComponent = (componentName: string, newProperties: Record<string, any>) => {
    setComponents(components.map(comp => 
      comp.name === componentName 
        ? { ...comp, properties: newProperties }
        : comp
    ));
  };

  const toggleComponent = (componentName: string) => {
    setComponents(components.map(comp =>
      comp.name === componentName 
        ? { ...comp, enabled: !comp.enabled }
        : comp
    ));
  };

  const openComponentForm = (component: ComponentInstance) => {
    setEditingComponent(component);
    setShowComponentForm(true);
  };

  const handleComponentFormSubmit = (values: Record<string, any>) => {
    if (editingComponent) {
      updateComponent(editingComponent.name, values);
      setShowComponentForm(false);
      setEditingComponent(null);

      toast({
        title: "Component updated",
        description: `${editingComponent.name} has been updated successfully.`
      });
    }
  };

  const applyItemPreset = (presetName: string) => {
    const presetComponents = applyPreset(presetName, 'item', availableComponents);
    setComponents(presetComponents);

    toast({
      title: "Preset applied",
      description: `Applied ${presetName} preset with ${presetComponents.length} components.`
    });
  };

  const resetItem = () => {
    setComponents([
      {
        name: 'minecraft:max_stack_size',
        enabled: true,
        properties: { value: 64 },
        metadata: { addedAt: Date.now(), category: 'Core', difficulty: 'beginner' }
      }
    ]);
    setIdentifier('');
    setDisplayName('');
    setDescription('');

    toast({
      title: "Item reset",
      description: "Item has been reset to default state."
    });
  };

  const exportToClipboard = () => {
    navigator.clipboard.writeText(JSON.stringify(itemJSON, null, 2));
    toast({
      title: "Copied to clipboard",
      description: "Item JSON has been copied to your clipboard."
    });
  };

  const componentsByCategory = useMemo(() => {
    const grouped: Record<string, ComponentInstance[]> = {};
    components.forEach(comp => {
      const category = comp.metadata?.category || 'Other';
      if (!grouped[category]) grouped[category] = [];
      grouped[category].push(comp);
    });
    return grouped;
  }, [components]);

  const getComponentDefinition = (componentName: string): ComponentDefinition | null => {
    const def = availableComponents.find(c => c.name === componentName);
    if (!def) return null;

    return {
      name: def.name,
      description: def.description,
      category: def.category,
      subcategory: def.subcategory,
      version: def.version,
      difficulty: def.difficulty,
      properties: def.properties,
      example: def.example,
      keywords: def.keywords,
      stability: def.stability,
      dependencies: def.dependencies,
      conflicts: def.conflicts
    };
  };

  // Tabs configuration
  const getComponentsTabLabel = () => {
    const enabledCount = components.filter(c => c.enabled).length;
    return `Components (${enabledCount})`;
  };

  const getAdvancedTabLabel = () => "Advanced";

  const tabsConfig = [
    {
      id: 'basic',
      title: 'Basic',
      icon: <Settings className="w-4 h-4" />,
      description: 'Item identifier and basic settings',
      content: (
        <div className="space-y-4">
          <div className="grid gap-4">
            <div>
              <Label htmlFor="item-identifier">Item Identifier *</Label>
              <Input
                id="item-identifier"
                value={identifier}
                onChange={(e) => setIdentifier(e.target.value)}
                placeholder="my_addon:custom_item"
                data-testid="input-identifier"
              />
              <p className="text-xs text-muted-foreground mt-1">
                Must include namespace (e.g., my_addon:item_name)
              </p>
            </div>

            <div>
              <Label htmlFor="item-display-name">Display Name</Label>
              <Input
                id="item-display-name"
                value={displayName}
                onChange={(e) => setDisplayName(e.target.value)}
                placeholder="Custom Item"
                data-testid="input-display-name"
              />
            </div>

            <div>
              <Label htmlFor="item-description">Description</Label>
              <Input
                id="item-description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                placeholder="A custom item for my addon"
                data-testid="input-description"
              />
            </div>

            <div>
              <Label htmlFor="menu-category">Menu Category</Label>
              <Select value={menuCategory} onValueChange={setMenuCategory}>
                <SelectTrigger data-testid="select-menu-category">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {menuCategories.map(cat => (
                    <SelectItem key={cat.value} value={cat.value}>
                      {cat.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
        </div>
      ),
    },
    {
      id: 'components',
      title: 'Components',
      badge: components.length,
      icon: <Layers className="w-4 h-4" />,
      description: 'Manage item components and their properties',
      content: (
        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <h4 className="font-medium">Item Components</h4>
            <Button 
              onClick={() => setShowComponentSelector(true)}
              data-testid="button-add-component"
            >
              Add Component
            </Button>
          </div>

          {recommendedComponents.length > 0 && (
            <Alert>
              <Info className="h-4 w-4" />
              <AlertDescription>
                <div className="space-y-2">
                  <p className="font-medium">Recommended components:</p>
                  <div className="flex flex-wrap gap-1">
                    {recommendedComponents.map(comp => (
                      <Button
                        key={comp.name}
                        variant="outline"
                        size="sm"
                        onClick={() => addComponent(comp.name)}
                        data-testid={`button-add-recommended-${comp.name}`}
                      >
                        {comp.name}
                      </Button>
                    ))}
                  </div>
                </div>
              </AlertDescription>
            </Alert>
          )}

          <ScrollArea className="h-[400px]">
            <Accordion type="single" collapsible className="space-y-2">
              {Object.entries(componentsByCategory).map(([category, categoryComponents]) => (
                <AccordionItem key={category} value={category}>
                  <AccordionTrigger className="text-sm" data-testid={`accordion-${category}`}>
                    {category} ({categoryComponents.length})
                  </AccordionTrigger>
                  <AccordionContent>
                    <div className="space-y-2">
                      {categoryComponents.map((component) => {
                        const def = getComponentDefinition(component.name);
                        return (
                          <Card key={component.name} className="p-3">
                            <div className="flex items-start justify-between gap-2">
                              <div className="flex-1 min-w-0">
                                <div className="flex items-center gap-2 mb-1">
                                  <Switch
                                    checked={component.enabled}
                                    onCheckedChange={() => toggleComponent(component.name)}
                                    data-testid={`switch-${component.name}`}
                                  />
                                  <span className="font-medium text-sm truncate">
                                    {component.name}
                                  </span>
                                  {def && (
                                    <Badge 
                                      variant="outline" 
                                      className={`text-xs ${
                                        def.difficulty === 'beginner' ? 'border-green-500' :
                                        def.difficulty === 'intermediate' ? 'border-yellow-500' :
                                        'border-red-500'
                                      }`}
                                    >
                                      {def.difficulty}
                                    </Badge>
                                  )}
                                </div>
                                {def && (
                                  <p className="text-xs text-muted-foreground line-clamp-2">
                                    {def.description}
                                  </p>
                                )}
                              </div>
                              <div className="flex gap-1">
                                <Tooltip>
                                  <TooltipTrigger asChild>
                                    <Button
                                      variant="ghost"
                                      size="sm"
                                      onClick={() => openComponentForm(component)}
                                      disabled={!component.enabled}
                                      data-testid={`button-configure-${component.name}`}
                                    >
                                      <Settings className="w-3 h-3" />
                                    </Button>
                                  </TooltipTrigger>
                                  <TooltipContent>Configure properties</TooltipContent>
                                </Tooltip>
                                <Tooltip>
                                  <TooltipTrigger asChild>
                                    <Button
                                      variant="ghost"
                                      size="sm"
                                      onClick={() => removeComponent(component.name)}
                                      data-testid={`button-remove-${component.name}`}
                                    >
                                      <AlertCircle className="w-3 h-3" />
                                    </Button>
                                  </TooltipTrigger>
                                  <TooltipContent>Remove component</TooltipContent>
                                </Tooltip>
                              </div>
                            </div>
                          </Card>
                        );
                      })}
                    </div>
                  </AccordionContent>
                </AccordionItem>
              ))}
            </Accordion>
          </ScrollArea>
        </div>
      ),
    },
    {
      id: 'presets',
      title: 'Presets',
      icon: <Zap className="w-4 h-4" />,
      description: 'Quick start with common item configurations',
      content: (
        <div className="space-y-4">
          <div>
            <h4 className="font-medium mb-3">Item Presets</h4>
            <p className="text-sm text-muted-foreground mb-4">
              Quick start with common item configurations
            </p>
            <div className="grid gap-3">
              {Object.entries(ITEM_PRESETS).map(([presetName, componentList]) => (
                <Card key={presetName} className="p-4 cursor-pointer hover:bg-muted/50">
                  <div className="flex items-center justify-between">
                    <div>
                      <h5 className="font-medium capitalize">
                        {presetName.replace(/_/g, ' ')}
                      </h5>
                      <p className="text-xs text-muted-foreground">
                        {componentList.length} components
                      </p>
                      <p className="text-xs text-muted-foreground mt-1">
                        {presetName === 'basic_item' && 'Simple stackable item'}
                        {presetName === 'food_item' && 'Consumable food with nutrition'}
                        {presetName === 'tool_item' && 'Durable tool with mining capability'}
                        {presetName === 'weapon_item' && 'Combat weapon with damage'}
                        {presetName === 'armor_item' && 'Wearable protection gear'}
                      </p>
                    </div>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => applyItemPreset(presetName)}
                      data-testid={`button-preset-${presetName}`}
                    >
                      Apply
                    </Button>
                  </div>
                </Card>
              ))}
            </div>
          </div>
        </div>
      ),
    },
    {
      id: 'advanced',
      title: 'Advanced',
      icon: <FileText className="w-4 h-4" />,
      description: 'Advanced configuration and statistics',
      content: (
        <div className="space-y-4">
          <div>
            <h4 className="font-medium mb-2">Actions</h4>
            <div className="flex gap-2">
              <Button
                variant="outline"
                onClick={resetItem}
                data-testid="button-reset"
              >
                <RotateCcw className="w-4 h-4 mr-2" />
                Reset
              </Button>
              <Button
                variant="outline"
                onClick={exportToClipboard}
                data-testid="button-export"
              >
                <Copy className="w-4 h-4 mr-2" />
                Copy JSON
              </Button>
            </div>
          </div>

          <div className="space-y-3">
            <h4 className="font-medium">Component Statistics</h4>
            <div className="grid grid-cols-2 gap-4 text-sm">
              <div className="bg-muted p-3 rounded">
                <div className="font-medium">Total Components</div>
                <div className="text-2xl font-bold">{components.length}</div>
              </div>
              <div className="bg-muted p-3 rounded">
                <div className="font-medium">Enabled Components</div>
                <div className="text-2xl font-bold">{components.filter(c => c.enabled).length}</div>
              </div>
            </div>
          </div>

          <ValidationStatus validation={validation} />
        </div>
      ),
    },
  ];

  return (
    <TooltipProvider>
      <section className="p-6" data-testid="item-builder">
        <div className="max-w-7xl mx-auto">
          <div className="grid lg:grid-cols-2 gap-6">
            {/* Builder Form */}
            <div className="space-y-6">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Package className="w-5 h-5" />
                    Item Configuration
                  </CardTitle>
                  <CardDescription>
                    Create comprehensive Minecraft Bedrock items with all official components
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <Tabs defaultValue="basic" className="space-y-4" data-testid="item-builder-tabs">
                    <TabsList className="grid w-full grid-cols-3">
                      <TabsTrigger value="basic" className="flex items-center gap-2">
                        <Settings className="w-4 h-4" />
                        Basic
                      </TabsTrigger>
                      <TabsTrigger value="components" className="flex items-center gap-2">
                        <Box className="w-4 h-4" />
                        {getComponentsTabLabel()}
                      </TabsTrigger>
                      <TabsTrigger value="advanced" className="flex items-center gap-2">
                        <Cog className="w-4 h-4" />
                        {getAdvancedTabLabel()}
                      </TabsTrigger>
                    </TabsList>

                    <TabsContent value="basic" className="space-y-4">
                      <div className="space-y-4">
                        <div>
                          <Label htmlFor="item-identifier">Item Identifier *</Label>
                          <Input
                            id="item-identifier"
                            value={identifier}
                            onChange={(e) => setIdentifier(e.target.value)}
                            placeholder="my_addon:custom_item"
                            data-testid="input-identifier"
                          />
                        </div>
                      </div>
                    </TabsContent>

                    <TabsContent value="components" className="space-y-4">
                      <div className="space-y-4">
                        <div className="flex items-center justify-between">
                          <h4 className="font-medium">Item Components</h4>
                          <Button 
                            onClick={() => setShowComponentSelector(true)}
                            data-testid="button-add-component"
                          >
                            Add Component
                          </Button>
                        </div>

                        {recommendedComponents.length > 0 && (
                          <Alert>
                            <Info className="h-4 w-4" />
                            <AlertDescription>
                              <div className="space-y-2">
                                <p className="font-medium">Recommended components:</p>
                                <div className="flex flex-wrap gap-1">
                                  {recommendedComponents.map(comp => (
                                    <Button
                                      key={comp.name}
                                      variant="outline"
                                      size="sm"
                                      onClick={() => addComponent(comp.name)}
                                      data-testid={`button-add-recommended-${comp.name}`}
                                    >
                                      {comp.name}
                                    </Button>
                                  ))}
                                </div>
                              </div>
                            </AlertDescription>
                          </Alert>
                        )}

                        <ScrollArea className="h-[400px]">
                          <Accordion type="single" collapsible className="space-y-2">
                            {Object.entries(componentsByCategory).map(([category, categoryComponents]) => (
                              <AccordionItem key={category} value={category}>
                                <AccordionTrigger className="text-sm" data-testid={`accordion-${category}`}>
                                  {category} ({categoryComponents.length})
                                </AccordionTrigger>
                                <AccordionContent>
                                  <div className="space-y-2">
                                    {categoryComponents.map((component) => {
                                      const def = getComponentDefinition(component.name);
                                      return (
                                        <Card key={component.name} className="p-3">
                                          <div className="flex items-start justify-between gap-2">
                                            <div className="flex-1 min-w-0">
                                              <div className="flex items-center gap-2 mb-1">
                                                <Switch
                                                  checked={component.enabled}
                                                  onCheckedChange={() => toggleComponent(component.name)}
                                                  data-testid={`switch-${component.name}`}
                                                />
                                                <span className="font-medium text-sm truncate">
                                                  {component.name}
                                                </span>
                                                {def && (
                                                  <Badge 
                                                    variant="outline" 
                                                    className={`text-xs ${
                                                      def.difficulty === 'beginner' ? 'border-green-500' :
                                                      def.difficulty === 'intermediate' ? 'border-yellow-500' :
                                                      'border-red-500'
                                                    }`}
                                                  >
                                                    {def.difficulty}
                                                  </Badge>
                                                )}
                                              </div>
                                              {def && (
                                                <p className="text-xs text-muted-foreground line-clamp-2">
                                                  {def.description}
                                                </p>
                                              )}
                                            </div>
                                            <div className="flex gap-1">
                                              <Tooltip>
                                                <TooltipTrigger asChild>
                                                  <Button
                                                    variant="ghost"
                                                    size="sm"
                                                    onClick={() => openComponentForm(component)}
                                                    disabled={!component.enabled}
                                                    data-testid={`button-configure-${component.name}`}
                                                  >
                                                    <Settings className="w-3 h-3" />
                                                  </Button>
                                                </TooltipTrigger>
                                                <TooltipContent>Configure properties</TooltipContent>
                                              </Tooltip>
                                              <Tooltip>
                                                <TooltipTrigger asChild>
                                                  <Button
                                                    variant="ghost"
                                                    size="sm"
                                                    onClick={() => removeComponent(component.name)}
                                                    data-testid={`button-remove-${component.name}`}
                                                  >
                                                    <AlertCircle className="w-3 h-3" />
                                                  </Button>
                                                </TooltipTrigger>
                                                <TooltipContent>Remove component</TooltipContent>
                                              </Tooltip>
                                            </div>
                                          </div>
                                        </Card>
                                      );
                                    })}
                                  </div>
                                </AccordionContent>
                              </AccordionItem>
                            ))}
                          </Accordion>
                        </ScrollArea>
                      </div>
                    </TabsContent>

                    <TabsContent value="advanced" className="space-y-4">
                      <div className="space-y-4">
                        <ValidationStatus validation={validation} />
                      </div>
                    </TabsContent>
                  </Tabs>
                </CardContent>
              </Card>
            </div>

            {/* JSON Preview */}
            <div className="space-y-6">
              <CodePreview 
                code={JSON.stringify(itemJSON, null, 2)}
                language="json"
                title="Item JSON"
              />
            </div>
          </div>
        </div>

        {/* Component Selector Modal */}
        {showComponentSelector && (
          <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
            <div className="bg-background rounded-lg shadow-lg max-w-4xl w-full max-h-[90vh] overflow-hidden">
              <ComponentSelector
                components={availableComponents}
                selectedComponents={selectedComponentNames}
                onAddComponent={addComponent}
                onRemoveComponent={removeComponent}
                title="Add Item Components"
                description="Choose from 25+ official Minecraft Bedrock item components"
                showCategories={true}
                showCompatibility={true}
              />
              <div className="p-4 border-t flex justify-end">
                <Button 
                  variant="outline" 
                  onClick={() => setShowComponentSelector(false)}
                  data-testid="button-close-selector"
                >
                  Close
                </Button>
              </div>
            </div>
          </div>
        )}

        {/* Component Form Modal */}
        {showComponentForm && editingComponent && (
          <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
            <div className="bg-background rounded-lg shadow-lg max-w-2xl w-full max-h-[90vh] overflow-hidden">
              {(() => {
                const def = getComponentDefinition(editingComponent.name);
                return def ? (
                  <ComponentForm
                    component={def}
                    initialValues={editingComponent.properties}
                    onSubmit={handleComponentFormSubmit}
                    onCancel={() => {
                      setShowComponentForm(false);
                      setEditingComponent(null);
                    }}
                    isEditing={true}
                    showExample={true}
                  />
                ) : null;
              })()}
            </div>
          </div>
        )}
      </section>
    </TooltipProvider>
  );
}

client/src/components/Builders/LootTable.content:
import { useState, useMemo } from "react";
import { Gift, Download, Copy, Save, RotateCcw, Info, Plus, Trash2, Settings, Dices, FileText, Layers } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Accordion, AccordionContent, AccordionItem, AccordionTrigger } from "@/components/ui/accordion";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "@/components/ui/tooltip";

import CodePreview from "@/components/Common/CodePreview";
import ValidationStatus from "@/components/Common/ValidationStatus";

import { useLocalStorage } from "@/hooks/useLocalStorage";
import { useToast } from "@/hooks/use-toast";
import { useCollapsible } from "@/hooks/useCollapsible";
import { CollapsibleSection, CollapsibleGroup } from "@/components/ui/collapsible-section";
import { validateLootTableJSON } from "@/lib/minecraft/validation";

// Import the comprehensive loot functions registry
import { lootFunctions, generateLootTableJSON } from "@shared/gameplayRegistry";

interface LootFunction {
  name: string;
  properties: Record<string, any>;
  enabled: boolean;
}

interface LootCondition {
  condition: string;
  properties: Record<string, any>;
  enabled: boolean;
}

interface LootEntry {
  type: string;
  name: string;
  weight: number;
  quality?: number;
  count?: { min: number; max: number };
  functions: LootFunction[];
  conditions: LootCondition[];
}

interface LootPool {
  rolls: { min: number; max: number };
  bonus_rolls?: { min: number; max: number };
  entries: LootEntry[];
  conditions: LootCondition[];
  functions: LootFunction[];
}

export default function LootTableBuilder() {
  const { toast } = useToast();

  // Basic loot table properties
  const [identifier, setIdentifier] = useLocalStorage('loot-identifier-v2', '');
  const [lootType, setLootType] = useLocalStorage('loot-type-v2', 'entity');
  const [description, setDescription] = useLocalStorage('loot-description-v2', '');

  // Pool management
  const [pools, setPools] = useLocalStorage<LootPool[]>('loot-pools-v2', [
    {
      rolls: { min: 1, max: 1 },
      entries: [
        {
          type: 'item',
          name: 'minecraft:iron_ingot',
          weight: 1,
          functions: [],
          conditions: []
        }
      ],
      conditions: [],
      functions: []
    }
  ]);

  // UI state
  const [activeTab, setActiveTab] = useState('basic');
  const [selectedPool, setSelectedPool] = useState<number>(0);
  const [selectedEntry, setSelectedEntry] = useState<number>(0);
  const [showFunctionSelector, setShowFunctionSelector] = useState(false);
  const [editingTarget, setEditingTarget] = useState<{ type: 'pool' | 'entry'; poolIndex: number; entryIndex?: number } | null>(null);

  // Available loot functions grouped by category
  const lootFunctionsByCategory = useMemo(() => {
    const grouped: Record<string, typeof lootFunctions> = {};
    lootFunctions.forEach(func => {
      const category = func.subcategory || func.category;
      if (!grouped[category]) grouped[category] = [];
      grouped[category].push(func);
    });
    return grouped;
  }, []);

  // Generate JSON
  const lootTableJSON = useMemo(() => {
    const processedPools = pools.map(pool => ({
      rolls: pool.rolls,
      ...(pool.bonus_rolls && { bonus_rolls: pool.bonus_rolls }),
      entries: pool.entries.map(entry => ({
        type: entry.type,
        name: entry.name,
        weight: entry.weight,
        ...(entry.quality && { quality: entry.quality }),
        ...(entry.count && { count: entry.count }),
        ...(entry.functions.length > 0 && {
          functions: entry.functions.filter(f => f.enabled).map(f => ({
            function: f.name,
            ...f.properties
          }))
        }),
        ...(entry.conditions.length > 0 && {
          conditions: entry.conditions.filter(c => c.enabled).map(c => ({
            condition: c.condition,
            ...c.properties
          }))
        })
      })),
      ...(pool.functions.length > 0 && {
        functions: pool.functions.filter(f => f.enabled).map(f => ({
          function: f.name,
          ...f.properties
        }))
      }),
      ...(pool.conditions.length > 0 && {
        conditions: pool.conditions.filter(c => c.enabled).map(c => ({
          condition: c.condition,
          ...c.properties
        }))
      })
    }));

    return {
      format_version: "1.21.0",
      pools: processedPools
    };
  }, [pools]);

  const validation = validateLootTableJSON(lootTableJSON);

  // Loot types
  const lootTypes = [
    { value: 'entity', label: 'Entity Drops', description: 'Loot from mob kills' },
    { value: 'blocks', label: 'Block Drops', description: 'Loot from block breaking' },
    { value: 'chests', label: 'Chest Loot', description: 'Loot in generated chests' },
    { value: 'fishing', label: 'Fishing Loot', description: 'Loot from fishing' },
    { value: 'archaeology', label: 'Archaeology Loot', description: 'Loot from brushing' },
    { value: 'gameplay', label: 'Gameplay Loot', description: 'Custom gameplay loot' }
  ];

  // Entry types
  const entryTypes = [
    { value: 'item', label: 'Item', description: 'Single item drop' },
    { value: 'loot_table', label: 'Loot Table', description: 'Reference to another loot table' },
    { value: 'empty', label: 'Empty', description: 'No drop (for weight balancing)' },
    { value: 'group', label: 'Group', description: 'Group of entries' }
  ];

  // Pool management functions
  const addPool = () => {
    setPools([...pools, {
      rolls: { min: 1, max: 1 },
      entries: [{ type: 'item', name: '', weight: 1, functions: [], conditions: [] }],
      conditions: [],
      functions: []
    }]);
  };

  const removePool = (poolIndex: number) => {
    setPools(pools.filter((_, i) => i !== poolIndex));
    if (selectedPool >= pools.length - 1) {
      setSelectedPool(Math.max(0, poolIndex - 1));
    }
  };

  const updatePool = (poolIndex: number, updates: Partial<LootPool>) => {
    setPools(pools.map((pool, i) => 
      i === poolIndex ? { ...pool, ...updates } : pool
    ));
  };

  // Entry management functions
  const addEntry = (poolIndex: number) => {
    const newEntry: LootEntry = {
      type: 'item',
      name: '',
      weight: 1,
      functions: [],
      conditions: []
    };

    updatePool(poolIndex, {
      entries: [...pools[poolIndex].entries, newEntry]
    });
  };

  const removeEntry = (poolIndex: number, entryIndex: number) => {
    updatePool(poolIndex, {
      entries: pools[poolIndex].entries.filter((_, i) => i !== entryIndex)
    });
  };

  const updateEntry = (poolIndex: number, entryIndex: number, updates: Partial<LootEntry>) => {
    updatePool(poolIndex, {
      entries: pools[poolIndex].entries.map((entry, i) =>
        i === entryIndex ? { ...entry, ...updates } : entry
      )
    });
  };

  // Function management
  const addFunctionToTarget = (functionName: string) => {
    const funcDef = lootFunctions.find(f => f.name === functionName);
    if (!funcDef || !editingTarget) return;

    // Generate default properties
    const defaultProperties: Record<string, any> = {};
    funcDef.properties.forEach(prop => {
      if (prop.default !== undefined) {
        defaultProperties[prop.name] = prop.default;
      } else {
        switch (prop.type) {
          case 'number':
            defaultProperties[prop.name] = prop.min ?? prop.example ?? 0;
            break;
          case 'boolean':
            defaultProperties[prop.name] = prop.example ?? false;
            break;
          case 'string':
            defaultProperties[prop.name] = prop.options?.[0] ?? prop.example ?? '';
            break;
          case 'range':
            defaultProperties[prop.name] = prop.example ?? { min: 1, max: 3 };
            break;
          case 'array':
            defaultProperties[prop.name] = prop.example ?? [];
            break;
          case 'object':
            defaultProperties[prop.name] = prop.example ?? {};
            break;
        }
      }
    });

    const newFunction: LootFunction = {
      name: functionName,
      properties: defaultProperties,
      enabled: true
    };

    if (editingTarget.type === 'pool') {
      const pool = pools[editingTarget.poolIndex];
      updatePool(editingTarget.poolIndex, {
        functions: [...pool.functions, newFunction]
      });
    } else if (editingTarget.type === 'entry' && editingTarget.entryIndex !== undefined) {
      const entry = pools[editingTarget.poolIndex].entries[editingTarget.entryIndex];
      updateEntry(editingTarget.poolIndex, editingTarget.entryIndex, {
        functions: [...entry.functions, newFunction]
      });
    }

    setShowFunctionSelector(false);
    setEditingTarget(null);

    toast({
      title: "Function added",
      description: `${functionName} has been added successfully.`
    });
  };

  const removeFunctionFromTarget = (functionIndex: number) => {
    if (!editingTarget) return;

    if (editingTarget.type === 'pool') {
      const pool = pools[editingTarget.poolIndex];
      updatePool(editingTarget.poolIndex, {
        functions: pool.functions.filter((_, i) => i !== functionIndex)
      });
    } else if (editingTarget.type === 'entry' && editingTarget.entryIndex !== undefined) {
      const entry = pools[editingTarget.poolIndex].entries[editingTarget.entryIndex];
      updateEntry(editingTarget.poolIndex, editingTarget.entryIndex, {
        functions: entry.functions.filter((_, i) => i !== functionIndex)
      });
    }
  };

  const resetLootTable = () => {
    setIdentifier('');
    setDescription('');
    setPools([{
      rolls: { min: 1, max: 1 },
      entries: [{ type: 'item', name: '', weight: 1, functions: [], conditions: [] }],
      conditions: [],
      functions: []
    }]);

    toast({
      title: "Loot table reset",
      description: "Loot table has been reset to default state."
    });
  };

  const exportToClipboard = () => {
    navigator.clipboard.writeText(JSON.stringify(lootTableJSON, null, 2));
    toast({
      title: "Copied to clipboard",
      description: "Loot table JSON has been copied to your clipboard."
    });
  };

  const calculateTotalWeight = (poolIndex: number): number => {
    return pools[poolIndex]?.entries.reduce((sum, entry) => sum + entry.weight, 0) || 0;
  };

  const calculateDropProbability = (poolIndex: number, entryIndex: number): number => {
    const totalWeight = calculateTotalWeight(poolIndex);
    const entryWeight = pools[poolIndex]?.entries[entryIndex]?.weight || 0;
    return totalWeight > 0 ? (entryWeight / totalWeight) * 100 : 0;
  };

  return (
    <TooltipProvider>
      <section className="p-6" data-testid="loot-table-builder">
        <div className="max-w-7xl mx-auto">
          <div className="grid lg:grid-cols-2 gap-6">
            {/* Builder Form */}
            <div className="space-y-6">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Gift className="w-5 h-5" />
                    Loot Table Configuration
                  </CardTitle>
                  <CardDescription>
                    Create comprehensive Minecraft Bedrock loot tables with all official functions
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <Tabs value={activeTab} onValueChange={setActiveTab}>
                    <TabsList className="grid w-full grid-cols-4">
                      <TabsTrigger value="basic" data-testid="tab-basic">Basic</TabsTrigger>
                      <TabsTrigger value="pools" data-testid="tab-pools">
                        Pools ({pools.length})
                      </TabsTrigger>
                      <TabsTrigger value="functions" data-testid="tab-functions">Functions</TabsTrigger>
                      <TabsTrigger value="advanced" data-testid="tab-advanced">Advanced</TabsTrigger>
                    </TabsList>

                    <TabsContent value="basic" className="space-y-4">
                      <div className="grid gap-4">
                        <div>
                          <Label htmlFor="loot-identifier">Loot Table Identifier *</Label>
                          <Input
                            id="loot-identifier"
                            value={identifier}
                            onChange={(e) => setIdentifier(e.target.value)}
                            placeholder="my_addon:custom_loot"
                            data-testid="input-identifier"
                          />
                          <p className="text-xs text-muted-foreground mt-1">
                            Must include namespace (e.g., my_addon:loot_name)
                          </p>
                        </div>

                        <div>
                          <Label htmlFor="loot-description">Description</Label>
                          <Input
                            id="loot-description"
                            value={description}
                            onChange={(e) => setDescription(e.target.value)}
                            placeholder="A custom loot table for my addon"
                            data-testid="input-description"
                          />
                        </div>

                        <div>
                          <Label htmlFor="loot-type">Loot Type</Label>
                          <Select value={lootType} onValueChange={setLootType}>
                            <SelectTrigger data-testid="select-loot-type">
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              {lootTypes.map(type => (
                                <SelectItem key={type.value} value={type.value}>
                                  <div>
                                    <div className="font-medium">{type.label}</div>
                                    <div className="text-xs text-muted-foreground">{type.description}</div>
                                  </div>
                                </SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                        </div>
                      </div>
                    </TabsContent>

                    <TabsContent value="pools" className="space-y-4">
                      <div className="flex items-center justify-between">
                        <h4 className="font-medium">Loot Pools</h4>
                        <Button onClick={addPool} data-testid="button-add-pool">
                          <Plus className="w-4 h-4 mr-2" />
                          Add Pool
                        </Button>
                      </div>

                      <ScrollArea className="h-[500px]">
                        <Accordion type="single" value={`pool-${selectedPool}`} onValueChange={(value) => {
                          const poolIndex = parseInt(value?.replace('pool-', '') || '0');
                          setSelectedPool(poolIndex);
                        }}>
                          {pools.map((pool, poolIndex) => (
                            <AccordionItem key={poolIndex} value={`pool-${poolIndex}`}>
                              <AccordionTrigger className="text-sm" data-testid={`accordion-pool-${poolIndex}`}>
                                Pool {poolIndex + 1} ({pool.entries.length} entries, weight: {calculateTotalWeight(poolIndex)})
                              </AccordionTrigger>
                              <AccordionContent>
                                <div className="space-y-4 p-2">
                                  {/* Pool Configuration */}
                                  <div className="grid grid-cols-2 gap-2">
                                    <div>
                                      <Label className="text-xs">Min Rolls</Label>
                                      <Input
                                        type="number"
                                        min="0"
                                        value={pool.rolls.min}
                                        onChange={(e) => updatePool(poolIndex, {
                                          rolls: { ...pool.rolls, min: parseInt(e.target.value) || 0 }
                                        })}
                                        data-testid={`input-pool-min-${poolIndex}`}
                                      />
                                    </div>
                                    <div>
                                      <Label className="text-xs">Max Rolls</Label>
                                      <Input
                                        type="number"
                                        min="0"
                                        value={pool.rolls.max}
                                        onChange={(e) => updatePool(poolIndex, {
                                          rolls: { ...pool.rolls, max: parseInt(e.target.value) || 0 }
                                        })}
                                        data-testid={`input-pool-max-${poolIndex}`}
                                      />
                                    </div>
                                  </div>

                                  {/* Pool Functions */}
                                  <div>
                                    <div className="flex items-center justify-between mb-2">
                                      <Label className="text-xs">Pool Functions ({pool.functions.length})</Label>
                                      <Button
                                        variant="outline"
                                        size="sm"
                                        onClick={() => {
                                          setEditingTarget({ type: 'pool', poolIndex });
                                          setShowFunctionSelector(true);
                                        }}
                                        data-testid={`button-add-pool-function-${poolIndex}`}
                                      >
                                        <Plus className="w-3 h-3 mr-1" />
                                        Add Function
                                      </Button>
                                    </div>
                                    {pool.functions.map((func, funcIndex) => (
                                      <div key={funcIndex} className="flex items-center gap-2 p-2 bg-muted rounded text-xs">
                                        <Switch
                                          checked={func.enabled}
                                          onCheckedChange={(enabled) => {
                                            const newFunctions = [...pool.functions];
                                            newFunctions[funcIndex] = { ...func, enabled };
                                            updatePool(poolIndex, { functions: newFunctions });
                                          }}
                                        />
                                        <span className="flex-1 font-mono">{func.name}</span>
                                        <Button
                                          variant="ghost"
                                          size="sm"
                                          onClick={() => removeFunctionFromTarget(funcIndex)}
                                          data-testid={`button-remove-pool-function-${poolIndex}-${funcIndex}`}
                                        >
                                          <Trash2 className="w-3 h-3" />
                                        </Button>
                                      </div>
                                    ))}
                                  </div>

                                  {/* Entries */}
                                  <div>
                                    <div className="flex items-center justify-between mb-2">
                                      <Label className="text-xs">Entries</Label>
                                      <Button
                                        variant="outline"
                                        size="sm"
                                        onClick={() => addEntry(poolIndex)}
                                        data-testid={`button-add-entry-${poolIndex}`}
                                      >
                                        <Plus className="w-3 h-3 mr-1" />
                                        Add Entry
                                      </Button>
                                    </div>
                                    {pool.entries.map((entry, entryIndex) => (
                                      <Card key={entryIndex} className="p-3 mb-2">
                                        <div className="grid gap-2">
                                          <div className="flex items-center gap-2">
                                            <Select
                                              value={entry.type}
                                              onValueChange={(type) => updateEntry(poolIndex, entryIndex, { type })}
                                            >
                                              <SelectTrigger className="w-24">
                                                <SelectValue />
                                              </SelectTrigger>
                                              <SelectContent>
                                                {entryTypes.map(type => (
                                                  <SelectItem key={type.value} value={type.value}>
                                                    {type.label}
                                                  </SelectItem>
                                                ))}
                                              </SelectContent>
                                            </Select>
                                            <Input
                                              placeholder="Item/Table name"
                                              value={entry.name}
                                              onChange={(e) => updateEntry(poolIndex, entryIndex, { name: e.target.value })}
                                              className="flex-1"
                                              data-testid={`input-entry-name-${poolIndex}-${entryIndex}`}
                                            />
                                            <Tooltip>
                                              <TooltipTrigger asChild>
                                                <Button
                                                  variant="ghost"
                                                  size="sm"
                                                  onClick={() => removeEntry(poolIndex, entryIndex)}
                                                  disabled={pool.entries.length === 1}
                                                  data-testid={`button-remove-entry-${poolIndex}-${entryIndex}`}
                                                >
                                                  <Trash2 className="w-3 h-3" />
                                                </Button>
                                              </TooltipTrigger>
                                              <TooltipContent>Remove entry</TooltipContent>
                                            </Tooltip>
                                          </div>

                                          <div className="grid grid-cols-3 gap-2 text-xs">
                                            <div>
                                              <Label>Weight</Label>
                                              <Input
                                                type="number"
                                                min="1"
                                                value={entry.weight}
                                                onChange={(e) => updateEntry(poolIndex, entryIndex, { weight: parseInt(e.target.value) || 1 })}
                                                data-testid={`input-entry-weight-${poolIndex}-${entryIndex}`}
                                              />
                                            </div>
                                            <div>
                                              <Label>Quality</Label>
                                              <Input
                                                type="number"
                                                value={entry.quality || ''}
                                                onChange={(e) => updateEntry(poolIndex, entryIndex, { quality: e.target.value ? parseInt(e.target.value) : undefined })}
                                                placeholder="Optional"
                                                data-testid={`input-entry-quality-${poolIndex}-${entryIndex}`}
                                              />
                                            </div>
                                            <div className="text-xs text-muted-foreground flex items-end">
                                              {calculateDropProbability(poolIndex, entryIndex).toFixed(1)}% chance
                                            </div>
                                          </div>

                                          <div>
                                            <div className="flex items-center justify-between mb-1">
                                              <Label className="text-xs">Entry Functions ({entry.functions.length})</Label>
                                              <Button
                                                variant="outline"
                                                size="sm"
                                                onClick={() => {
                                                  setEditingTarget({ type: 'entry', poolIndex, entryIndex });
                                                  setShowFunctionSelector(true);
                                                }}
                                                data-testid={`button-add-entry-function-${poolIndex}-${entryIndex}`}
                                              >
                                                <Settings className="w-3 h-3" />
                                              </Button>
                                            </div>
                                            {entry.functions.map((func, funcIndex) => (
                                              <div key={funcIndex} className="flex items-center gap-2 p-1 bg-muted rounded text-xs">
                                                <Switch
                                                  checked={func.enabled}
                                                  onCheckedChange={(enabled) => {
                                                    const newFunctions = [...entry.functions];
                                                    newFunctions[funcIndex] = { ...func, enabled };
                                                    updateEntry(poolIndex, entryIndex, { functions: newFunctions });
                                                  }}
                                                />
                                                <span className="flex-1 font-mono">{func.name}</span>
                                              </div>
                                            ))}
                                          </div>
                                        </div>
                                      </Card>
                                    ))}
                                  </div>

                                  <div className="flex justify-end">
                                    <Button
                                      variant="destructive"
                                      size="sm"
                                      onClick={() => removePool(poolIndex)}
                                      disabled={pools.length === 1}
                                      data-testid={`button-remove-pool-${poolIndex}`}
                                    >
                                      <Trash2 className="w-3 h-3 mr-1" />
                                      Remove Pool
                                    </Button>
                                  </div>
                                </div>
                              </AccordionContent>
                            </AccordionItem>
                          ))}
                        </Accordion>
                      </ScrollArea>
                    </TabsContent>

                    <TabsContent value="functions" className="space-y-4">
                      <div>
                        <h4 className="font-medium mb-3">Available Loot Functions</h4>
                        <p className="text-sm text-muted-foreground mb-4">
                          Functions modify how loot is generated and distributed
                        </p>
                        <ScrollArea className="h-[400px]">
                          <Accordion type="single" collapsible>
                            {Object.entries(lootFunctionsByCategory).map(([category, categoryFunctions]) => (
                              <AccordionItem key={category} value={category}>
                                <AccordionTrigger className="text-sm" data-testid={`accordion-function-${category}`}>
                                  {category} ({categoryFunctions.length} functions)
                                </AccordionTrigger>
                                <AccordionContent>
                                  <div className="grid gap-2">
                                    {categoryFunctions.map((func) => (
                                      <Card key={func.name} className="p-3">
                                        <div className="flex items-start justify-between gap-2">
                                          <div className="flex-1">
                                            <div className="flex items-center gap-2 mb-1">
                                              <span className="font-medium text-sm font-mono">{func.name}</span>
                                              <Badge 
                                                variant="outline" 
                                                className={`text-xs ${
                                                  func.difficulty === 'beginner' ? 'border-green-500' :
                                                  func.difficulty === 'intermediate' ? 'border-yellow-500' :
                                                  'border-red-500'
                                                }`}
                                              >
                                                {func.difficulty}
                                              </Badge>
                                            </div>
                                            <p className="text-xs text-muted-foreground">{func.description}</p>
                                          </div>
                                        </div>
                                      </Card>
                                    ))}
                                  </div>
                                </AccordionContent>
                              </AccordionItem>
                            ))}
                          </Accordion>
                        </ScrollArea>
                      </div>
                    </TabsContent>

                    <TabsContent value="advanced" className="space-y-4">
                      <div className="space-y-4">
                        <div>
                          <h4 className="font-medium mb-2">Actions</h4>
                          <div className="flex gap-2">
                            <Button
                              variant="outline"
                              onClick={resetLootTable}
                              data-testid="button-reset"
                            >
                              <RotateCcw className="w-4 h-4 mr-2" />
                              Reset
                            </Button>
                            <Button
                              variant="outline"
                              onClick={exportToClipboard}
                              data-testid="button-export"
                            >
                              <Copy className="w-4 h-4 mr-2" />
                              Copy JSON
                            </Button>
                          </div>
                        </div>

                        <div className="space-y-3">
                          <h4 className="font-medium">Loot Table Statistics</h4>
                          <div className="grid grid-cols-2 gap-4 text-sm">
                            <div className="bg-muted p-3 rounded">
                              <div className="font-medium">Total Pools</div>
                              <div className="text-2xl font-bold">{pools.length}</div>
                            </div>
                            <div className="bg-muted p-3 rounded">
                              <div className="font-medium">Total Entries</div>
                              <div className="text-2xl font-bold">{pools.reduce((sum, pool) => sum + pool.entries.length, 0)}</div>
                            </div>
                            <div className="bg-muted p-3 rounded">
                              <div className="font-medium">Active Functions</div>
                              <div className="text-2xl font-bold">
                                {pools.reduce((sum, pool) => 
                                  sum + pool.functions.filter(f => f.enabled).length + 
                                  pool.entries.reduce((entrySum, entry) => 
                                    entrySum + entry.functions.filter(f => f.enabled).length, 0
                                  ), 0
                                )}
                              </div>
                            </div>
                            <div className="bg-muted p-3 rounded">
                              <div className="font-medium">Average Weight</div>
                              <div className="text-2xl font-bold">
                                {pools.length > 0 ? 
                                  (pools.reduce((sum, pool) => sum + calculateTotalWeight(pools.indexOf(pool)), 0) / pools.length).toFixed(1) : 
                                  '0'
                                }
                              </div>
                            </div>
                          </div>
                        </div>

                        <ValidationStatus validation={validation} />
                      </div>
                    </TabsContent>
                  </Tabs>
                </CardContent>
              </Card>
            </div>

            {/* JSON Preview */}
            <div className="space-y-6">
              <CodePreview 
                code={JSON.stringify(lootTableJSON, null, 2)}
                language="json"
                title="Loot Table JSON"
                validation={validation}
              />
            </div>
          </div>
        </div>

        {/* Function Selector Modal */}
        {showFunctionSelector && (
          <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
            <div className="bg-background rounded-lg shadow-lg max-w-4xl w-full max-h-[90vh] overflow-hidden">
              <div className="p-4 border-b">
                <h3 className="text-lg font-semibold">Add Loot Function</h3>
                <p className="text-sm text-muted-foreground">
                  Choose a function to modify loot generation
                </p>
              </div>
              <ScrollArea className="max-h-[60vh] p-4">
                <Accordion type="single" collapsible>
                  {Object.entries(lootFunctionsByCategory).map(([category, categoryFunctions]) => (
                    <AccordionItem key={category} value={category}>
                      <AccordionTrigger>{category} ({categoryFunctions.length})</AccordionTrigger>
                      <AccordionContent>
                        <div className="grid gap-2">
                          {categoryFunctions.map((func) => (
                            <Card 
                              key={func.name} 
                              className="p-3 cursor-pointer hover:bg-muted/50"
                              onClick={() => addFunctionToTarget(func.name)}
                              data-testid={`card-function-${func.name}`}
                            >
                              <div className="flex items-center gap-2">
                                <div className="flex-1">
                                  <div className="font-medium text-sm font-mono">{func.name}</div>
                                  <p className="text-xs text-muted-foreground">{func.description}</p>
                                </div>
                                <Badge 
                                  variant="outline" 
                                  className={`text-xs ${
                                    func.difficulty === 'beginner' ? 'border-green-500' :
                                    func.difficulty === 'intermediate' ? 'border-yellow-500' :
                                    'border-red-500'
                                  }`}
                                >
                                  {func.difficulty}
                                </Badge>
                              </div>
                            </Card>
                          ))}
                        </div>
                      </AccordionContent>
                    </AccordionItem>
                  ))}
                </Accordion>
              </ScrollArea>
              <div className="p-4 border-t flex justify-end gap-2">
                <Button 
                  variant="outline" 
                  onClick={() => {
                    setShowFunctionSelector(false);
                    setEditingTarget(null);
                  }}
                  data-testid="button-close-function-selector"
                >
                  Cancel
                </Button>
              </div>
            </div>
          </div>
        )}
      </section>
    </TooltipProvider>
  );
}

client/src/components/Builders/RecipeBui.content:
import { useState, useMemo } from "react";
import { ChefHat, Download, Copy, Save, RotateCcw, Info, Plus, Trash2, Grid3X3, Shuffle, Flame, Beaker, Hammer, Settings, FileText, Layers } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "@/components/ui/tooltip";

import CodePreview from "@/components/Common/CodePreview";
import ValidationStatus from "@/components/Common/ValidationStatus";

import { useLocalStorage } from "@/hooks/useLocalStorage";
import { useToast } from "@/hooks/use-toast";
import { useCollapsible } from "@/hooks/useCollapsible";
import { CollapsibleSection, CollapsibleGroup } from "@/components/ui/collapsible-section";
import { validateRecipeJSON } from "@/lib/minecraft/validation";

// Import the comprehensive recipe registry
import { recipeTypes, generateRecipeJSON } from "@shared/gameplayRegistry";

interface RecipeIngredient {
  item: string;
  count?: number;
  data?: number;
  tag?: string;
}

interface ShapedRecipeKey {
  [key: string]: RecipeIngredient;
}

interface RecipeResult {
  item: string;
  count: number;
  data?: number;
}

export default function RecipeBuilder() {
  const { toast } = useToast();

  // Basic recipe properties
  const [identifier, setIdentifier] = useLocalStorage('recipe-identifier-v2', '');
  const [selectedRecipeType, setSelectedRecipeType] = useLocalStorage('recipe-selected-type-v2', 'minecraft:recipe_shaped');
  const [description, setDescription] = useLocalStorage('recipe-description-v2', '');
  const [tags, setTags] = useLocalStorage('recipe-tags-v2', 'crafting_table');
  const [group, setGroup] = useLocalStorage('recipe-group-v2', '');

  // Recipe-specific properties
  const [shapedPattern, setShapedPattern] = useLocalStorage<string[]>('recipe-shaped-pattern-v2', ['AAA', 'A A', 'AAA']);
  const [shapedKeys, setShapedKeys] = useLocalStorage<ShapedRecipeKey>('recipe-shaped-keys-v2', { 'A': { item: 'minecraft:planks' } });
  const [shapelessIngredients, setShapelessIngredients] = useLocalStorage<RecipeIngredient[]>('recipe-shapeless-ingredients-v2', [{ item: 'minecraft:planks' }]);
  const [furnaceInput, setFurnaceInput] = useLocalStorage<RecipeIngredient>('recipe-furnace-input-v2', { item: 'minecraft:raw_iron' });
  const [brewingInput, setBrewingInput] = useLocalStorage<RecipeIngredient>('recipe-brewing-input-v2', { item: 'minecraft:potion', tag: 'minecraft:awkward_potion' });
  const [brewingReagent, setBrewingReagent] = useLocalStorage<RecipeIngredient>('recipe-brewing-reagent-v2', { item: 'minecraft:spider_eye' });
  const [smithingTemplate, setSmithingTemplate] = useLocalStorage<RecipeIngredient>('recipe-smithing-template-v2', { item: 'minecraft:netherite_upgrade_smithing_template' });
  const [smithingBase, setSmithingBase] = useLocalStorage<RecipeIngredient>('recipe-smithing-base-v2', { item: 'minecraft:diamond_sword' });
  const [smithingAddition, setSmithingAddition] = useLocalStorage<RecipeIngredient>('recipe-smithing-addition-v2', { item: 'minecraft:netherite_ingot' });

  // Result configuration
  const [result, setResult] = useLocalStorage<RecipeResult>('recipe-result-v2', { item: 'minecraft:chest', count: 1 });

  // UI state
  const [activeTab, setActiveTab] = useState('basic');

  // Get current recipe type definition
  const currentRecipeTypeDef = recipeTypes.find(r => r.name === selectedRecipeType);

  // Generate JSON based on recipe type
  const recipeJSON = useMemo(() => {
    const baseDescription = {
      identifier: identifier || "my_addon:custom_recipe"
    };

    let recipeProperties: Record<string, any> = {};

    switch (selectedRecipeType) {
      case 'minecraft:recipe_shaped':
        recipeProperties = {
          tags: tags.split(',').map(t => t.trim()).filter(t => t),
          pattern: shapedPattern,
          key: shapedKeys,
          result: result,
          ...(group && { group })
        };
        break;

      case 'minecraft:recipe_shapeless':
        recipeProperties = {
          tags: tags.split(',').map(t => t.trim()).filter(t => t),
          ingredients: shapelessIngredients,
          result: result,
          ...(group && { group })
        };
        break;

      case 'minecraft:recipe_furnace':
        recipeProperties = {
          tags: tags.split(',').map(t => t.trim()).filter(t => t),
          input: furnaceInput,
          output: result.item,
          ...(group && { group })
        };
        break;

      case 'minecraft:recipe_brewing_mix':
        recipeProperties = {
          tags: tags.split(',').map(t => t.trim()).filter(t => t),
          input: brewingInput,
          reagent: brewingReagent,
          output: result.item,
          ...(group && { group })
        };
        break;

      case 'minecraft:recipe_brewing_container':
        recipeProperties = {
          tags: tags.split(',').map(t => t.trim()).filter(t => t),
          input: brewingInput,
          reagent: brewingReagent,
          output: result.item,
          ...(group && { group })
        };
        break;

      case 'minecraft:recipe_smithing_transform':
        recipeProperties = {
          tags: tags.split(',').map(t => t.trim()).filter(t => t),
          template: smithingTemplate,
          base: smithingBase,
          addition: smithingAddition,
          result: result,
          ...(group && { group })
        };
        break;

      case 'minecraft:recipe_smithing_trim':
        recipeProperties = {
          tags: tags.split(',').map(t => t.trim()).filter(t => t),
          template: smithingTemplate,
          base: smithingBase,
          addition: smithingAddition,
          ...(group && { group })
        };
        break;

      default:
        recipeProperties = {
          tags: tags.split(',').map(t => t.trim()).filter(t => t),
          result: result
        };
    }

    return {
      format_version: "1.21.0",
      [selectedRecipeType]: {
        description: baseDescription,
        ...recipeProperties
      }
    };
  }, [
    identifier, selectedRecipeType, tags, group, shapedPattern, shapedKeys, 
    shapelessIngredients, furnaceInput, brewingInput, brewingReagent, 
    smithingTemplate, smithingBase, smithingAddition, result
  ]);

  const validation = validateRecipeJSON(recipeJSON);

  // Recipe type categories
  const recipeTypesByCategory = useMemo(() => {
    const grouped: Record<string, typeof recipeTypes> = {};
    recipeTypes.forEach(recipe => {
      if (!grouped[recipe.category]) grouped[recipe.category] = [];
      grouped[recipe.category].push(recipe);
    });
    return grouped;
  }, []);

  // Recipe management functions
  const resetRecipe = () => {
    setIdentifier('');
    setDescription('');
    setTags('crafting_table');
    setGroup('');
    setShapedPattern(['AAA', 'A A', 'AAA']);
    setShapedKeys({ 'A': { item: 'minecraft:planks' } });
    setShapelessIngredients([{ item: 'minecraft:planks' }]);
    setResult({ item: 'minecraft:chest', count: 1 });

    toast({
      title: "Recipe reset",
      description: "Recipe has been reset to default state."
    });
  };

  const exportToClipboard = () => {
    navigator.clipboard.writeText(JSON.stringify(recipeJSON, null, 2));
    toast({
      title: "Copied to clipboard",
      description: "Recipe JSON has been copied to your clipboard."
    });
  };

  const addShapedKey = () => {
    const availableKeys = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('').filter(k => !shapedKeys[k]);
    if (availableKeys.length > 0) {
      setShapedKeys({ ...shapedKeys, [availableKeys[0]]: { item: 'minecraft:stone' } });
    }
  };

  const removeShapedKey = (key: string) => {
    const newKeys = { ...shapedKeys };
    delete newKeys[key];
    setShapedKeys(newKeys);
  };

  const updateShapedKey = (key: string, ingredient: RecipeIngredient) => {
    setShapedKeys({ ...shapedKeys, [key]: ingredient });
  };

  const addShapelessIngredient = () => {
    setShapelessIngredients([...shapelessIngredients, { item: 'minecraft:stone' }]);
  };

  const removeShapelessIngredient = (index: number) => {
    setShapelessIngredients(shapelessIngredients.filter((_, i) => i !== index));
  };

  const updateShapelessIngredient = (index: number, ingredient: RecipeIngredient) => {
    setShapelessIngredients(shapelessIngredients.map((ing, i) => i === index ? ingredient : ing));
  };

  const getRecipeTypeIcon = (recipeType: string) => {
    switch (recipeType) {
      case 'minecraft:recipe_shaped': return <Grid3X3 className="w-4 h-4" />;
      case 'minecraft:recipe_shapeless': return <Shuffle className="w-4 h-4" />;
      case 'minecraft:recipe_furnace': return <Flame className="w-4 h-4" />;
      case 'minecraft:recipe_brewing_mix':
      case 'minecraft:recipe_brewing_container': return <Beaker className="w-4 h-4" />;
      case 'minecraft:recipe_smithing_transform':
      case 'minecraft:recipe_smithing_trim': return <Hammer className="w-4 h-4" />;
      default: return <ChefHat className="w-4 h-4" />;
    }
  };

  return (
    <TooltipProvider>
      <section className="p-6" data-testid="recipe-builder">
        <div className="max-w-7xl mx-auto">
          <div className="grid lg:grid-cols-2 gap-6">
            {/* Builder Form */}
            <div className="space-y-6">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <ChefHat className="w-5 h-5" />
                    Recipe Configuration
                  </CardTitle>
                  <CardDescription>
                    Create comprehensive Minecraft Bedrock recipes with all official recipe types
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <Tabs value={activeTab} onValueChange={setActiveTab}>
                    <TabsList className="grid w-full grid-cols-4">
                      <TabsTrigger value="basic" data-testid="tab-basic">Basic</TabsTrigger>
                      <TabsTrigger value="type" data-testid="tab-type">Type & Config</TabsTrigger>
                      <TabsTrigger value="result" data-testid="tab-result">Result</TabsTrigger>
                      <TabsTrigger value="advanced" data-testid="tab-advanced">Advanced</TabsTrigger>
                    </TabsList>

                    <TabsContent value="basic" className="space-y-4">
                      <div className="grid gap-4">
                        <div>
                          <Label htmlFor="recipe-identifier">Recipe Identifier *</Label>
                          <Input
                            id="recipe-identifier"
                            value={identifier}
                            onChange={(e) => setIdentifier(e.target.value)}
                            placeholder="my_addon:custom_recipe"
                            data-testid="input-identifier"
                          />
                          <p className="text-xs text-muted-foreground mt-1">
                            Must include namespace (e.g., my_addon:recipe_name)
                          </p>
                        </div>

                        <div>
                          <Label htmlFor="recipe-description">Description</Label>
                          <Input
                            id="recipe-description"
                            value={description}
                            onChange={(e) => setDescription(e.target.value)}
                            placeholder="A custom recipe for my addon"
                            data-testid="input-description"
                          />
                        </div>

                        <div>
                          <Label htmlFor="recipe-tags">Recipe Tags</Label>
                          <Input
                            id="recipe-tags"
                            value={tags}
                            onChange={(e) => setTags(e.target.value)}
                            placeholder="crafting_table, furnace"
                            data-testid="input-tags"
                          />
                          <p className="text-xs text-muted-foreground mt-1">
                            Comma-separated list (e.g., crafting_table, stonecutter)
                          </p>
                        </div>

                        <div>
                          <Label htmlFor="recipe-group">Recipe Group (optional)</Label>
                          <Input
                            id="recipe-group"
                            value={group}
                            onChange={(e) => setGroup(e.target.value)}
                            placeholder="wooden_tools"
                            data-testid="input-group"
                          />
                          <p className="text-xs text-muted-foreground mt-1">
                            Groups recipes together in the recipe book
                          </p>
                        </div>
                      </div>
