        { id: 'builder-recipe', icon: 'recipeBuilder', label: 'Recipe Builder' },
        { id: 'builder-loot', icon: 'lootBuilder', label: 'Loot Tables' },
        { id: 'builder-biome', icon: 'biomeBuilder', label: 'Biome Builder' },
