                      glyphMargin: false,  // No glyph margin to save space
                      lineDecorationsWidth: 0,  // No decoration width
                      folding: true,  // Enable code folding
                      foldingMaximumRegions: 5000,  // Allow many folding regions
                      foldingStrategy: 'indentation',  // Use indentation for folding

                      showFoldingControls: 'mouseover',  // Show folding on hover

                      // WORD WRAPPING - CURSOR-FRIENDLY
                      wordWrap: 'off',  // No wrapping to maintain cursor accuracy
                      wordWrapColumn: 120,  // Wider wrap column if enabled
                      wrappingIndent: 'indent',  // Proper indentation for wrapped lines
                      wrappingStrategy: 'advanced',  // Better wrapping algorithm

                      // PERFORMANCE OPTIMIZATIONS FOR CURSOR TRACKING
                      suggest: {
                        insertMode: 'replace',  // Replace mode for suggestions
                        filterGraceful: true,  // Graceful filtering
                        showIcons: true,  // Show suggestion icons
                        showSnippets: true,  // Show snippets
                        showWords: false,  // Don't show word suggestions (reduces interference)
                        localityBonus: true  // Boost local suggestions
                      },

                      // EDITOR BEHAVIOR FOR ACCURATE POSITIONING
                      stickyTabStops: false,  // Don't use sticky tab stops
                      tabCompletion: 'on',  // Enable tab completion
                      useTabStops: true,  // Use tab stops for indentation
                      detectIndentation: true,  // Auto-detect indentation
                      insertSpaces: true,  // Use spaces instead of tabs
                      tabSize: 2,  // 2-space tabs
                      trimAutoWhitespace: true,  // Trim auto whitespace

                      // ACCESSIBILITY AND FOCUS
                      ariaLabel: 'Script Studio Code Editor',
                      readOnly: false,
                      domReadOnly: false,
                      accessibilitySupport: 'auto'
                    }}
                    data-testid="monaco-editor"
                  />
                </div>
              </div>

              {/* Bottom Panel - Documentation & Tools (Collapsible) */}
              {documentationOpen && (
                <>
                  <ResizableHandle withHandle />
                  <div className="h-48 border-t bg-muted/10">
                    <Tabs defaultValue="docs" className="h-full flex flex-col">
                      <TabsList className="grid w-full grid-cols-3 h-8">
                        <TabsTrigger value="docs" className="text-xs">
                          <Book className="w-3 h-3 mr-1" />
                          Docs
                        </TabsTrigger>
                        <TabsTrigger value="tools" className="text-xs">
                          <Zap className="w-3 h-3 mr-1" />
                          Tools
                        </TabsTrigger>
                        <TabsTrigger value="templates" className="text-xs">
                          <Settings className="w-3 h-3 mr-1" />
                          Templates
                        </TabsTrigger>
                      </TabsList>

                      <TabsContent value="docs" className="flex-1 p-3 overflow-auto">
                        {selectedElement ? (
                          <div>
                            <div className="flex items-center justify-between mb-3">
                              <div className="flex items-center gap-2">
                                {getElementIcon(selectedElement.type)}
                                <div>
                                  <h4 className="font-semibold text-sm">{selectedElement.name}</h4>
                                  <p className="text-xs text-muted-foreground">{selectedElement.module}</p>
                                </div>
                              </div>
                              <Button
                                size="sm"
                                onClick={() => insertCode(`${selectedElement.name}`)}
                                data-testid="button-insert-code"
                              >
                                <Plus className="w-3 h-3 mr-1" />
                                Insert
                              </Button>
                            </div>
                            <p className="text-sm">{selectedElement.element.description}</p>
                          </div>
                        ) : (
                          <div className="text-center py-8">
                            <Book className="w-8 h-8 mx-auto mb-2 text-muted-foreground" />
                            <p className="text-sm text-muted-foreground">Select an API element to view documentation</p>
                          </div>
                        )}
                      </TabsContent>

                      <TabsContent value="tools" className="flex-1 p-3">
                        <div className="text-center py-8">
                          <Terminal className="w-8 h-8 mx-auto mb-2 text-muted-foreground" />
                          <p className="text-sm text-muted-foreground">Code tools and utilities</p>
                        </div>
                      </TabsContent>

                      <TabsContent value="templates" className="flex-1 p-3 overflow-auto">
                        <div className="grid grid-cols-2 gap-2">
                          {[
                            {
                              name: "Hello World",
                              code: `import { world } from '@minecraft/server';\\nworld.sendMessage("Hello!");`
                            },
                            {
                              name: "Player Events",
                              code: `import { world } from '@minecraft/server';\\nworld.afterEvents.playerJoin.subscribe((event) => {\\n  world.sendMessage(\`Welcome \${event.player.name}!\`);\\n});`
                            }
                          ].map((template, idx) => (
                            <Card key={idx} className="cursor-pointer hover:bg-muted/50" onClick={() => insertCode(template.code)}>
                              <CardHeader className="p-2">
                                <CardTitle className="text-xs">{template.name}</CardTitle>
                              </CardHeader>
                            </Card>
                          ))}
                        </div>
                      </TabsContent>
                    </Tabs>
                  </div>
                </>
              )}

              {/* Toggle Documentation Panel */}
              <div className="absolute bottom-2 right-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setDocumentationOpen(!documentationOpen)}
                  className="h-8"
                >
                  {documentationOpen ? <ChevronDown className="w-3 h-3" /> : <ChevronRight className="w-3 h-3" />}
                </Button>
              </div>
            </div>
          </ResizablePanel>
        </ResizablePanelGroup>
      </div>
    </section>
  );
}

