                    e.stopPropagation();
                    if (feature.section === 'pixilart-external') {
                      window.open('https://www.pixilart.com/draw?ref=home-page', '_blank', 'noopener,noreferrer');
                    } else {
                      onNavigate(feature.section);
                    }
                  }}
                >
