 */

export interface MinecraftDoc {
  id: string;
  title: string;
  content: string;
  category: string;
  version: string;
  lastUpdated: string;
  officialUrl?: string;
}

export interface APIMethod {
  name: string;
  description: string;
  module: string;
  parameters: Array<{
    name: string;
    type: string;
    description: string;
    optional?: boolean;
    defaultValue?: any;
  }>;
  returns: {
    type: string;
    description: string;
  };
  examples: string[];
  version: string;
  deprecated?: boolean;
}

export interface ComponentSpec {
  name: string;
  description: string;
  category: string;
  properties: Array<{
    name: string;
    type: string;
    description: string;
    required?: boolean;
    defaultValue?: any;
