    subcategories: ['Basic', 'Advanced', 'Navigation', 'Jump', 'Environmental']
  },
  'Behavior': {
    name: 'AI Behaviors',
    description: 'AI goals and behavioral patterns for entities',
    icon: 'brain_coral.png',
    subcategories: ['Movement', 'Social', 'Defensive', 'Survival', 'Idle', 'Family']
  },
