import { z } from 'zod';

// Base interfaces for different gameplay elements
export interface RecipeType {
  name: string;
  description: string;
  category: 'Crafting' | 'Smelting' | 'Brewing' | 'Smithing' | 'Special';
  subcategory?: string;
  version: string;
  difficulty: 'beginner' | 'intermediate' | 'advanced';
  properties: GameplayProperty[];
  example: string;
  keywords: string[];
  stability: 'stable' | 'experimental' | 'beta';
}

export interface LootFunction {
  name: string;
  description: string;
  category: 'Loot' | 'Trading';
  subcategory?: string;
  version: string;
  difficulty: 'beginner' | 'intermediate' | 'advanced';
  properties: GameplayProperty[];
  example: string;
  keywords: string[];
  stability: 'stable' | 'experimental' | 'beta';
}

export interface BiomeComponent {
  name: string;
  description: string;
  category: 'Biome';
  subcategory?: string;
  version: string;
  difficulty: 'beginner' | 'intermediate' | 'advanced';
  properties: GameplayProperty[];
  example: string;
  keywords: string[];
  stability: 'stable' | 'experimental' | 'beta';
}

export interface SpawnRuleComponent {
  name: string;
  description: string;
  category: 'Spawning';
  subcategory?: string;
  version: string;
  difficulty: 'beginner' | 'intermediate' | 'advanced';
  properties: GameplayProperty[];
  example: string;
  keywords: string[];
  stability: 'stable' | 'experimental' | 'beta';
}

export interface GameplayProperty {
  name: string;
  type: 'number' | 'boolean' | 'string' | 'array' | 'object' | 'range' | 'filter' | 'condition';
  description: string;
  required?: boolean;
  default?: any;
  min?: number;
  max?: number;
  options?: string[];
  example?: any;
}

// Zod schemas
export const GameplayPropertySchema = z.object({
  name: z.string(),
  type: z.enum(['number', 'boolean', 'string', 'array', 'object', 'range', 'filter', 'condition']),
  description: z.string(),
  required: z.boolean().optional(),
  default: z.any().optional(),
  min: z.number().optional(),
  max: z.number().optional(),
  options: z.array(z.string()).optional(),
  example: z.any().optional(),
});

