                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => openInNewWindow(tool)}
                            title="Open in new window"
                            className="min-h-[44px] min-w-[44px] p-2"
                          >
                            <ExternalLink className="w-4 h-4" />
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={exitFullscreen}
                            title="Exit fullscreen (Esc)"
                            className="min-h-[44px] min-w-[44px] p-2"
                          >
                            <X className="w-4 h-4" />
                          </Button>
